import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import RecipeService from '../services/RecipeService';
import { useUser } from './UserContext';
import '../styles/Recipe.css';
import Navbar from '../components/Navbar';

const RecipeDetail = () => {
    const { id } = useParams();
    const [recipe, setRecipe] = useState(null);
    const [loading, setLoading] = useState(true);
    const [comment, setComment] = useState('');
    const [comments, setComments] = useState([]);
    const [submitting, setSubmitting] = useState(false);
    const { user } = useUser();
    const navigate = useNavigate();

    useEffect(() => {
        fetchRecipe();
        fetchComments();
    }, [id]);

    const fetchRecipe = () => {
        RecipeService.getRecipeById(id)
            .then(response => {
                setRecipe(response.data);
                setLoading(false);
            })
            .catch(error => {
                console.error('Error fetching recipe:', error);
                setLoading(false);
            });
    };

    const fetchComments = () => {
        RecipeService.getComments(id)
            .then(response => {
                setComments(response.data);
            })
            .catch(error => {
                console.error('Error fetching comments:', error);
            });
    };

    const handleDelete = () => {
        if (window.confirm('Are you sure you want to delete this recipe?')) {
            RecipeService.deleteRecipe(id, user.id)
                .then(() => {
                    navigate('/recipes');
                })
                .catch(error => {
                    console.error('Error deleting recipe:', error);
                    alert('Failed to delete recipe. Please try again.');
                });
        }
    };

    const handleSubmitComment = (e) => {
        e.preventDefault();
        if (!comment.trim()) return;
        
        setSubmitting(true);
        const commentData = {
            content: comment
        };
        
        RecipeService.addComment(id, commentData, user.id)
            .then(response => {
                setComments([response.data, ...comments]);
                setComment('');
                setSubmitting(false);
            })
            .catch(error => {
                console.error('Error adding comment:', error);
                setSubmitting(false);
                alert('Failed to add comment. Please try again.');
            });
    };

    const formatDate = (timestamp) => {
        return new Date(timestamp).toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'short',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    if (loading) {
        return (
            <>
                <Navbar />
                <div className="recipe-container">
                    <p>Loading recipe...</p>
                </div>
            </>
        );
    }

    if (!recipe) {
        return (
            <>
                <Navbar />
                <div className="recipe-container">
                    <p>Recipe not found.</p>
                    <Link to="/recipes" className="btn btn-primary">Back to Recipes</Link>
                </div>
            </>
        );
    }

    return (
        <>
            <Navbar />
            <div className="recipe-detail">
                <div className="recipe-detail-header">
                    <h1 className="recipe-detail-title">{recipe.title}</h1>
                    <div className="recipe-detail-meta">
                        <p className="recipe-detail-author">By {recipe.userName} â€¢ {formatDate(recipe.createdAt)}</p>
                        {recipe.cuisine && (
                            <span className="recipe-detail-cuisine">{recipe.cuisine}</span>
                        )}
                    </div>
                </div>

                {recipe.imageUrl && (
                    <img
                        src={recipe.imageUrl}
                        alt={recipe.title}
                        className="recipe-detail-image"
                    />
                )}

                <div className="recipe-detail-stats">
                    <div className="recipe-stat">
                        <div className="recipe-stat-value">{recipe.prepTimeMinutes}</div>
                        <div className="recipe-stat-label">Prep Time (min)</div>
                    </div>
                    <div className="recipe-stat">
                        <div className="recipe-stat-value">{recipe.cookTimeMinutes}</div>
                        <div className="recipe-stat-label">Cook Time (min)</div>
                    </div>
                    <div className="recipe-stat">
                        <div className="recipe-stat-value">{recipe.servings}</div>
                        <div className="recipe-stat-label">Servings</div>
                    </div>
                </div>

                {recipe.dietaryPreferences && recipe.dietaryPreferences.length > 0 && (
                    <div className="recipe-dietary-preferences">
                        {recipe.dietaryPreferences.map((pref, index) => (
                            <span key={index} className="recipe-dietary-tag">{pref}</span>
                        ))}
                    </div>
                )}

                <div className="recipe-detail-section">
                    <h2 className="recipe-detail-section-title">Ingredients</h2>
                    <ul className="recipe-ingredients-list">
                        {recipe.ingredients.map((ingredient, index) => (
                            <li key={index} className="recipe-ingredient-item">{ingredient}</li>
                        ))}
                    </ul>
                </div>

                <div className="recipe-detail-section">
                    <h2 className="recipe-detail-section-title">Instructions</h2>
                    <div className="recipe-instructions">
                        {recipe.instructions}
                    </div>
                </div>

                {user && user.id === recipe.userId && (
                    <div className="form-actions">
                        <Link to={`/recipes/${recipe.id}/edit`} className="btn btn-secondary">Edit Recipe</Link>
                        <button onClick={handleDelete} className="btn btn-danger">Delete Recipe</button>
                    </div>
                )}

                <div className="recipe-comments">
                    <h2 className="recipe-comments-title">Comments</h2>
                    
                    {user ? (
                        <form onSubmit={handleSubmitComment} className="comment-form">
                            <textarea
                                placeholder="Add a comment..."
                                value={comment}
                                onChange={(e) => setComment(e.target.value)}
                                required
                            />
                            <button type="submit" className="btn btn-primary" disabled={submitting}>
                                {submitting ? 'Posting...' : 'Post Comment'}
                            </button>
                        </form>
                    ) : (
                        <p>Please <Link to="/">log in</Link> to add a comment.</p>
                    )}
                    
                    <div className="comment-list">
                        {comments.length === 0 ? (
                            <p>No comments yet. Be the first to comment!</p>
                        ) : (
                            comments.map(comment => (
                                <div key={comment.id} className="comment-item">
                                    <div className="comment-header">
                                        <span className="comment-author">{comment.userName}</span>
                                        <span className="comment-date">{formatDate(comment.createdAt)}</span>
                                    </div>
                                    <div className="comment-content">{comment.content}</div>
                                </div>
                            ))
                        )}
                    </div>
                </div>
            </div>
        </>
    );
};

export default RecipeDetail;
