import axios from 'axios';

const API_URL = '/api/recipes';

class RecipeService {
    // Get all recipes
    getAllRecipes() {
        return axios.get(API_URL);
    }
    
    // Get recipe by ID
    getRecipeById(recipeId) {
        return axios.get(`${API_URL}/${recipeId}`);
    }
    
    // Get recipes by user
    getRecipesByUser(userId) {
        return axios.get(`${API_URL}/user`, {
            headers: { 'userid': userId }
        });
    }
    
    // Create new recipe
    createRecipe(recipeData, userId) {
        // Make sure userId is properly formatted as a number
        const numericUserId = Number(userId);
        
        // Log the request for debugging
        console.log('Creating recipe with userId:', numericUserId);
        
        // Sanitize the recipe data to ensure no undefined values
        const sanitizedData = JSON.parse(JSON.stringify(recipeData));
        
        // Use a proper Content-Type header and ensure userId is in both header and body
        return axios.post(API_URL, sanitizedData, {
            headers: { 
                'userid': numericUserId,
                'Content-Type': 'application/json'
            }
        });
    }
    
    // Update recipe
    updateRecipe(recipeId, recipeData, userId) {
        return axios.put(`${API_URL}/${recipeId}`, recipeData, {
            headers: { 'userid': userId }
        });
    }
    
    // Delete recipe
    deleteRecipe(recipeId, userId) {
        return axios.delete(`${API_URL}/${recipeId}`, {
            headers: { 'userid': userId }
        });
    }
    
    // Search recipes
    searchRecipes(query) {
        return axios.get(`${API_URL}/search?query=${query}`);
    }
    
    // Filter recipes
    filterRecipes(cuisine, dietary, ingredient) {
        let url = `${API_URL}/filter?`;
        if (cuisine) url += `cuisine=${cuisine}&`;
        if (dietary) url += `dietary=${dietary}&`;
        if (ingredient) url += `ingredient=${ingredient}&`;
        return axios.get(url);
    }
    
    // Add comment to recipe
    addComment(recipeId, comment, userId) {
        return axios.post(`${API_URL}/${recipeId}/comments`, comment, {
            headers: { 'userid': userId }
        });
    }
    
    // Get comments for a recipe
    getRecipeComments(recipeId) {
        return axios.get(`${API_URL}/${recipeId}/comments`);
    }
    
    // Add a debug test method
    debugCreateRecipe(recipeData, userId) {
        const numericUserId = Number(userId);
        console.log('DEBUG - Testing recipe creation with userId:', numericUserId);
        
        const sanitizedData = JSON.parse(JSON.stringify(recipeData));
        
        return axios.post(`${API_URL}/debug`, sanitizedData, {
            headers: { 
                'userid': numericUserId,
                'Content-Type': 'application/json'
            }
        });
    }
}

export default new RecipeService();
