import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RecipeService from '../services/RecipeService';
import { useUser } from './UserContext';

const RecipeList = () => {
  const [recipes, setRecipes] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [cuisineFilter, setCuisineFilter] = useState('');
  const [ingredientFilter, setIngredientFilter] = useState('');
  const [dietaryFilter, setDietaryFilter] = useState([]);
  const { user } = useUser();
  const navigate = useNavigate();

  const cuisineTypes = [
    'American', 'Italian', 'Mexican', 'Chinese', 'Indian', 
    'Japanese', 'Thai', 'Mediterranean', 'French', 'Greek'
  ];
  
  const dietaryOptions = [
    'Vegetarian', 'Vegan', 'Gluten-Free', 'Dairy-Free', 
    'Nut-Free', 'Low-Carb', 'Keto', 'Paleo'
  ];

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = () => {
    setLoading(true);
    RecipeService.getAllRecipes()
      .then(response => {
        setRecipes(response.data);
        setFilteredRecipes(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching recipes:', error);
        setLoading(false);
      });
  };

  useEffect(() => {
    // Apply filters whenever filter states change
    applyFilters();
  }, [searchTerm, cuisineFilter, ingredientFilter, dietaryFilter, recipes]);

  const applyFilters = () => {
    let result = [...recipes];
    
    // Apply search term filter
    if (searchTerm) {
      result = result.filter(recipe => 
        recipe.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        recipe.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    
    // Apply cuisine filter
    if (cuisineFilter) {
      result = result.filter(recipe => 
        recipe.cuisineType === cuisineFilter
      );
    }
    
    // Apply ingredient filter (simplified client-side filtering)
    if (ingredientFilter) {
      result = result.filter(recipe => 
        recipe.ingredients.some(ingredient => 
          ingredient.name.toLowerCase().includes(ingredientFilter.toLowerCase())
        )
      );
    }
    
    // Apply dietary preferences filter
    if (dietaryFilter.length > 0) {
      result = result.filter(recipe => 
        dietaryFilter.every(pref => 
          recipe.dietaryPreferences.includes(pref)
        )
      );
    }
    
    setFilteredRecipes(result);
  };

  const handleDietaryFilterChange = (preference) => {
    setDietaryFilter(prev => {
      if (prev.includes(preference)) {
        return prev.filter(p => p !== preference);
      } else {
        return [...prev, preference];
      }
    });
  };

  const resetFilters = () => {
    setSearchTerm('');
    setCuisineFilter('');
    setIngredientFilter('');
    setDietaryFilter([]);
    setFilteredRecipes(recipes);
  };

  return (
    <div className="recipe-list-container">
      <div className="recipe-list-header">
        <h1>Recipe Collection</h1>
        <div className="recipe-actions">
          <button 
            className="create-recipe-btn"
            onClick={() => navigate('/recipes/create')}
          >
            Create New Recipe
          </button>
          <button 
            className="my-recipes-btn"
            onClick={() => navigate('/recipes/my-recipes')}
          >
            My Recipes
          </button>
          <button 
            className="home-btn"
            onClick={() => navigate('/home')}
          >
            Back to Home
          </button>
        </div>
      </div>
      
      <div className="filter-section">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search recipes..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        
        <div className="filters">
          <div className="filter-group">
            <label>Cuisine:</label>
            <select 
              value={cuisineFilter} 
              onChange={(e) => setCuisineFilter(e.target.value)}
            >
              <option value="">All Cuisines</option>
              {cuisineTypes.map(cuisine => (
                <option key={cuisine} value={cuisine}>{cuisine}</option>
              ))}
            </select>
          </div>
          
          <div className="filter-group">
            <label>Ingredient:</label>
            <input
              type="text"
              placeholder="Filter by ingredient..."
              value={ingredientFilter}
              onChange={(e) => setIngredientFilter(e.target.value)}
            />
          </div>
          
          <div className="filter-group dietary-filter">
            <label>Dietary Preferences:</label>
            <div className="dietary-options">
              {dietaryOptions.map(option => (
                <label key={option} className="dietary-option">
                  <input
                    type="checkbox"
                    checked={dietaryFilter.includes(option)}
                    onChange={() => handleDietaryFilterChange(option)}
                  />
                  {option}
                </label>
              ))}
            </div>
          </div>
          
          <button className="reset-filters-btn" onClick={resetFilters}>
            Reset Filters
          </button>
        </div>
      </div>
      
      {loading ? (
        <div className="loading">Loading recipes...</div>
      ) : filteredRecipes.length > 0 ? (
        <div className="recipes-grid">
          {filteredRecipes.map(recipe => (
            <div 
              key={recipe.id} 
              className="recipe-card"
              onClick={() => navigate(`/recipes/${recipe.id}`)}
            >
              <div className="recipe-image-container">
                <img 
                  src={recipe.imageUrl || 'https://via.placeholder.com/300x200?text=No+Image'} 
                  alt={recipe.title} 
                  className="recipe-image" 
                />
              </div>
              <div className="recipe-info">
                <h3>{recipe.title}</h3>
                <p className="recipe-description">{recipe.description}</p>
                <div className="recipe-meta">
                  <span className="cuisine-type">{recipe.cuisineType}</span>
                  <span className="comments-count">{recipe.commentsCount} comments</span>
                </div>
                <p className="recipe-creator">By: {recipe.userName}</p>
                {recipe.dietaryPreferences && recipe.dietaryPreferences.length > 0 && (
                  <div className="dietary-tags">
                    {recipe.dietaryPreferences.map(pref => (
                      <span key={pref} className="dietary-tag">{pref}</span>
                    ))}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-recipes">
          <p>No recipes found matching your criteria.</p>
          <button onClick={resetFilters}>Clear filters</button>
        </div>
      )}
    </div>
  );
};

export default RecipeList;
