import axios from 'axios';

const API_URL = '/api/recipes';

class RecipeService {
    // Get all recipes
    getAllRecipes() {
        return axios.get(API_URL);
    }
    
    // Get recipe by ID
    getRecipeById(recipeId) {
        return axios.get(`${API_URL}/${recipeId}`);
    }
    
    // Get recipes by user
    getRecipesByUser(userId) {
        return axios.get(`${API_URL}/user`, {
            headers: { 'userid': userId }
        });
    }
    
    // Create new recipe
    createRecipe(recipeData, userId) {
        return axios.post(API_URL, recipeData, {
            headers: { 'userid': userId }
        });
    }
    
    // Update recipe
    updateRecipe(recipeId, recipeData, userId) {
        return axios.put(`${API_URL}/${recipeId}`, recipeData, {
            headers: { 'userid': userId }
        });
    }
    
    // Delete recipe
    deleteRecipe(recipeId, userId) {
        return axios.delete(`${API_URL}/${recipeId}`, {
            headers: { 'userid': userId }
        });
    }
    
    // Search recipes
    searchRecipes(query) {
        return axios.get(`${API_URL}/search?query=${query}`);
    }
    
    // Filter recipes
    filterRecipes(cuisine, dietary, ingredient) {
        let url = `${API_URL}/filter?`;
        if (cuisine) url += `cuisine=${cuisine}&`;
        if (dietary) url += `dietary=${dietary}&`;
        if (ingredient) url += `ingredient=${ingredient}&`;
        return axios.get(url);
    }
    
    // Add comment to recipe
    addComment(recipeId, comment, userId) {
        return axios.post(`${API_URL}/${recipeId}/comments`, comment, {
            headers: { 'userid': userId }
        });
    }
    
    // Get comments for a recipe
    getRecipeComments(recipeId) {
        return axios.get(`${API_URL}/${recipeId}/comments`);
    }
}

export default new RecipeService();
