import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import GroupService from '../services/GroupService';
import Swal from 'sweetalert2';
import '../styles/GroupDetail.css';

const GroupDetail = () => {
  const { groupId } = useParams();
  const navigate = useNavigate();
  const [group, setGroup] = useState(null);
  const [members, setMembers] = useState([]);
  const [moderators, setModerators] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userRole, setUserRole] = useState({
    isMember: false,
    isModerator: false,
    isAdmin: false
  });
  
  // Group messaging state
  const [messages, setMessages] = useState([
    // Sample messages for UI demonstration
    {
      id: 1,
      userId: 2,
      userName: "Jane Doe",
      content: "Has anyone tried the new pasta recipe that was shared last week?",
      timestamp: new Date(Date.now() - 86400000).toISOString() // 1 day ago
    },
    {
      id: 2,
      userId: 3,
      userName: "John Smith",
      content: "Yes! I made it last night. The garlic and herb sauce was amazing. I added some extra red pepper flakes for heat.",
      timestamp: new Date(Date.now() - 43200000).toISOString() // 12 hours ago
    }
  ]);
  const [newMessage, setNewMessage] = useState('');
  const [activeTab, setActiveTab] = useState('discussions');
  const messagesEndRef = useRef(null);
  
  // Mock user ID (would come from auth context in a real app)
  const currentUserId = 1;
  const currentUserName = "Current User"; // Would come from auth context

  useEffect(() => {
    loadGroupData();
  }, [groupId]);
  
  useEffect(() => {
    if (activeTab === 'messages' && messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages, activeTab]);

  const loadGroupData = async () => {
    setLoading(true);
    try {
      // Load group details
      const groupResponse = await GroupService.getGroupById(groupId);
      setGroup(groupResponse.data);
      
      // Load members
      const membersResponse = await GroupService.getGroupMembers(groupId);
      setMembers(membersResponse.data);
      
      // Load moderators
      const moderatorsResponse = await GroupService.getGroupModerators(groupId);
      setModerators(moderatorsResponse.data);
      
      // Check user roles
      const [isMemberRes, isModeratorRes, isAdminRes] = await Promise.all([
        GroupService.isMember(groupId, currentUserId),
        GroupService.isModerator(groupId, currentUserId),
        GroupService.isAdmin(groupId, currentUserId)
      ]);
      
      setUserRole({
        isMember: isMemberRes.data,
        isModerator: isModeratorRes.data,
        isAdmin: isAdminRes.data
      });
      
      setError(null);
    } catch (err) {
      setError('Failed to load group details. Please try again later.');
      console.error('Error loading group details:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleJoinGroup = async () => {
    try {
      await GroupService.joinGroup(groupId, currentUserId);
      Swal.fire({
        icon: 'success',
        title: 'Joined Successfully',
        text: 'You are now a member of this group'
      });
      loadGroupData(); // Refresh data
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Failed to Join',
        text: 'There was an error joining the group'
      });
      console.error('Error joining group:', err);
    }
  };

  const handleLeaveGroup = async () => {
    try {
      const result = await Swal.fire({
        icon: 'warning',
        title: 'Leave Group?',
        text: 'Are you sure you want to leave this group?',
        showCancelButton: true,
        confirmButtonText: 'Yes, Leave',
        cancelButtonText: 'Cancel'
      });
      
      if (result.isConfirmed) {
        await GroupService.leaveGroup(groupId, currentUserId);
        Swal.fire({
          icon: 'success',
          title: 'Left Successfully',
          text: 'You have left the group'
        });
        loadGroupData(); // Refresh data
      }
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Failed to Leave',
        text: err.response?.data?.message || 'There was an error leaving the group'
      });
      console.error('Error leaving group:', err);
    }
  };

  const handleDeleteGroup = async () => {
    try {
      const result = await Swal.fire({
        icon: 'warning',
        title: 'Delete Group?',
        text: 'This action cannot be undone. All group data will be permanently deleted.',
        showCancelButton: true,
        confirmButtonText: 'Yes, Delete',
        cancelButtonText: 'Cancel',
        confirmButtonColor: '#d33'
      });
      
      if (result.isConfirmed) {
        await GroupService.deleteGroup(groupId, currentUserId);
        Swal.fire({
          icon: 'success',
          title: 'Group Deleted',
          text: 'The group has been successfully deleted'
        });
        navigate('/groups'); // Redirect to groups list
      }
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Failed to Delete',
        text: 'There was an error deleting the group'
      });
      console.error('Error deleting group:', err);
    }
  };

  const handleAddModerator = async (userId) => {
    try {
      await GroupService.addModerator(groupId, userId, currentUserId);
      Swal.fire({
        icon: 'success',
        title: 'Moderator Added',
        text: 'The user is now a moderator'
      });
      loadGroupData(); // Refresh data
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Failed to Add Moderator',
        text: 'There was an error adding the moderator'
      });
      console.error('Error adding moderator:', err);
    }
  };

  const handleRemoveModerator = async (userId) => {
    try {
      await GroupService.removeModerator(groupId, userId, currentUserId);
      Swal.fire({
        icon: 'success',
        title: 'Moderator Removed',
        text: 'The user is no longer a moderator'
      });
      loadGroupData(); // Refresh data
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Failed to Remove Moderator',
        text: 'There was an error removing the moderator'
      });
      console.error('Error removing moderator:', err);
    }
  };
  
  const handleRemoveMember = async (userId) => {
    try {
      const result = await Swal.fire({
        icon: 'warning',
        title: 'Remove Member?',
        text: 'Are you sure you want to remove this member from the group?',
        showCancelButton: true,
        confirmButtonText: 'Yes, Remove',
        cancelButtonText: 'Cancel',
        confirmButtonColor: '#d33'
      });
      
      if (result.isConfirmed) {
        // In a real app, this would call an API endpoint to remove the member
        // await GroupService.removeMember(groupId, userId, currentUserId);
        
        // For demo, we'll just update the local state
        setMembers(members.filter(member => member.id !== userId));
        
        Swal.fire({
          icon: 'success',
          title: 'Member Removed',
          text: 'The member has been removed from the group'
        });
      }
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Failed to Remove Member',
        text: 'There was an error removing the member'
      });
      console.error('Error removing member:', err);
    }
  };

  const handleTransferOwnership = async () => {
    try {
      const { value: newAdminId } = await Swal.fire({
        title: 'Transfer Ownership',
        input: 'select',
        inputOptions: Object.fromEntries(
          members
            .filter(member => member.id !== currentUserId)
            .map(member => [member.id, member.name])
        ),
        inputPlaceholder: 'Select a new admin',
        showCancelButton: true
      });
      
      if (newAdminId) {
        await GroupService.transferOwnership(groupId, Number(newAdminId), currentUserId);
        Swal.fire({
          icon: 'success',
          title: 'Ownership Transferred',
          text: 'Group ownership has been transferred successfully'
        });
        loadGroupData(); // Refresh data
      }
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Failed to Transfer Ownership',
        text: 'There was an error transferring ownership'
      });
      console.error('Error transferring ownership:', err);
    }
  };
  
  const handleSendMessage = (e) => {
    e.preventDefault();
    
    if (!newMessage.trim()) return;
    
    const newMessageObj = {
      id: messages.length + 1,
      userId: currentUserId,
      userName: currentUserName,
      content: newMessage,
      timestamp: new Date().toISOString()
    };
    
    setMessages([...messages, newMessageObj]);
    setNewMessage('');
  };
  
  const formatMessageDate = (timestamp) => {
    const date = new Date(timestamp);
    return new Intl.DateTimeFormat('en-US', {
      month: 'short',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
    }).format(date);
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading group details...</p>
      </div>
    );
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!group) {
    return <div className="error-message">Group not found</div>;
  }

  const isModerator = userId => 
    moderators.some(moderator => moderator.id === userId);
    
  const getMemberById = (userId) => {
    return members.find(member => member.id === userId) || { name: 'Unknown User' };
  };
  
  // Get default image based on cuisine type
  const getGroupImage = () => {
    const defaultImages = {
      'Italian': 'https://images.unsplash.com/photo-1498579150354-977475b7ea0b?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Mexican': 'https://images.unsplash.com/photo-1599974579688-8dbdd335c77f?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Chinese': 'https://images.unsplash.com/photo-1563245372-f21724e3856d?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Indian': 'https://images.unsplash.com/photo-1585937421612-70a008356c36?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Japanese': 'https://images.unsplash.com/photo-1553621042-f6e147245754?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Thai': 'https://images.unsplash.com/photo-1559847844-5315695dadae?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Mediterranean': 'https://images.unsplash.com/photo-1556804335-2fa741d43774?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60'
    };
    
    return group.imageUrl || defaultImages[group.cuisineType] || 'https://images.unsplash.com/photo-1476718406336-bb5a9690ee2a?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60';
  };

  return (
    <div className="group-detail-container">
      <div className="group-header">
        <div className="group-image" style={{ backgroundImage: `url(${getGroupImage()})` }}>
          <div className="group-header-content">
            <h2>{group.name}</h2>
            <p className="cuisine-type">Cuisine: {group.cuisineType}</p>
          </div>
        </div>
      </div>
      
      <div className="group-actions">
        {!userRole.isMember && (
          <button className="join-btn" onClick={handleJoinGroup}>Join Group</button>
        )}
        
        {userRole.isMember && !userRole.isAdmin && (
          <button className="leave-btn" onClick={handleLeaveGroup}>Leave Group</button>
        )}
        
        {userRole.isAdmin && (
          <>
            <button className="edit-btn" onClick={() => navigate(`/groups/${groupId}/edit`)}>
              Edit Group
            </button>
            <button className="transfer-btn" onClick={handleTransferOwnership}>
              Transfer Ownership
            </button>
            <button className="delete-btn" onClick={handleDeleteGroup}>
              Delete Group
            </button>
          </>
        )}
      </div>
      
      <div className="group-content">
        <div className="group-sidebar">
          <div className="group-description">
            <h3>About</h3>
            <p>{group.description}</p>
          </div>
          
          <div className="group-members">
            <h3>Members ({members.length})</h3>
            <div className="members-list">
              {members.map(member => (
                <div key={member.id} className="member-card">
                  <div className="member-info">
                    <span className="member-avatar">{member.name.charAt(0)}</span>
                    <span className="member-name">{member.name}</span>
                    {member.id === group.adminId && (
                      <span className="admin-badge">Admin</span>
                    )}
                    {isModerator(member.id) && member.id !== group.adminId && (
                      <span className="moderator-badge">Moderator</span>
                    )}
                  </div>
                  
                  {userRole.isAdmin && member.id !== currentUserId && member.id !== group.adminId && (
                    <div className="member-actions">
                      {!isModerator(member.id) ? (
                        <button 
                          className="make-moderator-btn"
                          onClick={() => handleAddModerator(member.id)}
                        >
                          Make Mod
                        </button>
                      ) : (
                        <button 
                          className="remove-moderator-btn"
                          onClick={() => handleRemoveModerator(member.id)}
                        >
                          Remove Mod
                        </button>
                      )}
                      <button
                        className="remove-member-btn"
                        onClick={() => handleRemoveMember(member.id)}
                      >
                        Remove
                      </button>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
        
        <div className="group-main-content">
          <div className="content-tabs">
            <button 
              className={`tab-btn ${activeTab === 'discussions' ? 'active' : ''}`}
              onClick={() => setActiveTab('discussions')}
            >
              Discussions
            </button>
            <button 
              className={`tab-btn ${activeTab === 'messages' ? 'active' : ''}`}
              onClick={() => setActiveTab('messages')}
            >
              Group Chat
            </button>
          </div>
          
          {activeTab === 'discussions' ? (
            <div className="group-discussions">
              {userRole.isMember && (
                <button 
                  className="new-discussion-btn"
                  onClick={() => navigate(`/groups/${groupId}/discussions/create`)}
                >
                  Start New Discussion
                </button>
              )}
              <div className="coming-soon">
                <img src="https://images.unsplash.com/photo-1604908812255-27454ced7208?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60" alt="Coming Soon"/>
                <p>Discussion boards coming soon!</p>
                <p>Share your recipes, cooking tips, and more with the community.</p>
              </div>
            </div>
          ) : (
            <div className="group-messages">
              {userRole.isMember ? (
                <>
                  <div className="messages-container">
                    {messages.length === 0 ? (
                      <div className="no-messages">
                        <p>No messages yet. Be the first to say hello!</p>
                      </div>
                    ) : (
                      messages.map(message => (
                        <div 
                          key={message.id} 
                          className={`message ${message.userId === currentUserId ? 'own-message' : ''}`}
                        >
                          {message.userId !== currentUserId && (
                            <div className="message-sender">
                              <div className="message-avatar">
                                {message.userName.charAt(0)}
                              </div>
                              <span className="message-name">{message.userName}</span>
                            </div>
                          )}
                          <div className="message-bubble">
                            <p className="message-content">{message.content}</p>
                            <span className="message-time">{formatMessageDate(message.timestamp)}</span>
                          </div>
                        </div>
                      ))
                    )}
                    <div ref={messagesEndRef} />
                  </div>
                  
                  <form className="message-form" onSubmit={handleSendMessage}>
                    <input 
                      type="text"
                      value={newMessage}
                      onChange={(e) => setNewMessage(e.target.value)}
                      placeholder="Type your message..."
                      maxLength={500}
                    />
                    <button type="submit" disabled={!newMessage.trim()}>Send</button>
                  </form>
                </>
              ) : (
                <div className="not-member-message">
                  <p>You need to join this group to participate in group messaging.</p>
                  <button className="join-btn" onClick={handleJoinGroup}>Join Group</button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default GroupDetail;
