import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import RecipeService from '../services/RecipeService';
import { UserContext } from './UserContext';
import Navbar from '../components/Navbar';
import '../styles/RecipeDetail.css';

const RecipeDetail = () => {
    const { id } = useParams();
    const { user } = useContext(UserContext);
    const navigate = useNavigate();
    
    const [recipe, setRecipe] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [newComment, setNewComment] = useState('');
    const [comments, setComments] = useState([]);
    const [commentLoading, setCommentLoading] = useState(false);
    
    useEffect(() => {
        fetchRecipe();
        fetchComments();
    }, [id]);
    
    const fetchRecipe = async () => {
        try {
            const response = await RecipeService.getRecipeById(id);
            setRecipe(response.data);
            setLoading(false);
        } catch (err) {
            console.error('Error fetching recipe:', err);
            setError('Failed to load recipe. Please try again.');
            setLoading(false);
        }
    };
    
    const fetchComments = async () => {
        try {
            const response = await RecipeService.getRecipeComments(id);
            setComments(response.data);
        } catch (err) {
            console.error('Error fetching comments:', err);
        }
    };
    
    const handleDelete = async () => {
        if (window.confirm('Are you sure you want to delete this recipe? This action cannot be undone.')) {
            try {
                await RecipeService.deleteRecipe(id, user.id);
                navigate('/recipes');
            } catch (err) {
                console.error('Error deleting recipe:', err);
                alert('Failed to delete recipe. Please try again.');
            }
        }
    };
    
    const handleCommentSubmit = async (e) => {
        e.preventDefault();
        
        if (!user) {
            alert('Please log in to comment on recipes');
            return;
        }
        
        if (!newComment.trim()) {
            return;
        }
        
        setCommentLoading(true);
        
        try {
            const commentData = {
                content: newComment
            };
            
            await RecipeService.addComment(id, commentData, user.id);
            setNewComment('');
            fetchComments(); // Refresh comments after adding
            setCommentLoading(false);
        } catch (err) {
            console.error('Error adding comment:', err);
            setCommentLoading(false);
        }
    };
    
    if (loading) {
        return (
            <div className="recipe-detail-container">
                <Navbar />
                <div className="loading">Loading recipe...</div>
            </div>
        );
    }
    
    if (error || !recipe) {
        return (
            <div className="recipe-detail-container">
                <Navbar />
                <div className="error-message">{error || 'Recipe not found'}</div>
                <Link to="/recipes" className="back-link">Back to Recipes</Link>
            </div>
        );
    }
    
    const isOwner = user && recipe.userId === user.id;
    
    return (
        <div className="recipe-detail-container">
            <Navbar />
            <div className="recipe-detail-content">
                <div className="recipe-header">
                    <h1>{recipe.title}</h1>
                    <div className="recipe-meta">
                        <span className="recipe-author">By: {recipe.userName}</span>
                        {recipe.cuisine && (
                            <span className="recipe-cuisine">Cuisine: {recipe.cuisine}</span>
                        )}
                        {recipe.dietaryPreferences && (
                            <span className="recipe-dietary">Dietary: {recipe.dietaryPreferences}</span>
                        )}
                    </div>
                </div>
                
                <div className="recipe-actions">
                    <Link to="/recipes" className="back-link">Back to Recipes</Link>
                    {isOwner && (
                        <>
                            <Link to={`/recipes/${id}/edit`} className="edit-btn">Edit Recipe</Link>
                            <button onClick={handleDelete} className="delete-btn">Delete Recipe</button>
                        </>
                    )}
                </div>
                
                <div className="recipe-content">
                    <div className="recipe-image-container">
                        <img 
                            src={recipe.imageUrl || 'https://via.placeholder.com/600x400?text=No+Image'} 
                            alt={recipe.title} 
                            className="recipe-image"
                        />
                    </div>
                    
                    <div className="recipe-description">
                        <h2>Description</h2>
                        <p>{recipe.description}</p>
                    </div>
                    
                    <div className="recipe-ingredients">
                        <h2>Ingredients</h2>
                        <ul className="ingredients-list">
                            {recipe.ingredients.map((ingredient, index) => (
                                <li key={index} className="ingredient-item">
                                    <span className="ingredient-name">{ingredient.name}</span>
                                    <span className="ingredient-quantity">
                                        {ingredient.quantity} {ingredient.unit}
                                    </span>
                                </li>
                            ))}
                        </ul>
                    </div>
                    
                    <div className="recipe-instructions">
                        <h2>Instructions</h2>
                        <div className="instructions-content">
                            {recipe.instructions.split('\n').map((paragraph, index) => (
                                paragraph.trim() && <p key={index}>{paragraph}</p>
                            ))}
                        </div>
                    </div>
                    
                    <div className="recipe-comments">
                        <h2>Comments</h2>
                        
                        {user ? (
                            <form className="comment-form" onSubmit={handleCommentSubmit}>
                                <textarea
                                    placeholder="Add a comment..."
                                    value={newComment}
                                    onChange={(e) => setNewComment(e.target.value)}
                                    required
                                />
                                <button 
                                    type="submit"
                                    disabled={commentLoading || !newComment.trim()}
                                    className="comment-btn"
                                >
                                    {commentLoading ? 'Posting...' : 'Post Comment'}
                                </button>
                            </form>
                        ) : (
                            <p className="login-to-comment">
                                <Link to="/">Log in</Link> to comment on this recipe
                            </p>
                        )}
                        
                        <div className="comments-list">
                            {comments.length > 0 ? (
                                comments.map((comment) => (
                                    <div key={comment.id} className="comment">
                                        <div className="comment-header">
                                            <span className="comment-author">{comment.userName}</span>
                                            <span className="comment-date">
                                                {new Date(comment.createdAt).toLocaleDateString()}
                                            </span>
                                        </div>
                                        <p className="comment-content">{comment.content}</p>
                                    </div>
                                ))
                            ) : (
                                <p className="no-comments">No comments yet. Be the first to comment!</p>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default RecipeDetail;
