import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import GroupService from '../services/GroupService';
import '../styles/GroupList.css';

const GroupList = () => {
  const [groups, setGroups] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentFilter, setCurrentFilter] = useState('all');
  
  // Mock user ID (would come from auth context in a real app)
  const currentUserId = 1;

  useEffect(() => {
    loadGroups();
  }, [currentFilter]);

  const loadGroups = async () => {
    setLoading(true);
    try {
      let response;
      
      switch(currentFilter) {
        case 'member':
          response = await GroupService.getGroupsByMember(currentUserId);
          break;
        case 'admin':
          response = await GroupService.getGroupsByAdmin(currentUserId);
          break;
        default:
          response = await GroupService.getAllGroups();
      }
      
      setGroups(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to load groups. Please try again later.');
      console.error('Error loading groups:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) {
      loadGroups();
      return;
    }
    
    setLoading(true);
    try {
      const response = await GroupService.searchGroups(searchQuery);
      setGroups(response.data);
      setError(null);
    } catch (err) {
      setError('Search failed. Please try again.');
      console.error('Error searching groups:', err);
    } finally {
      setLoading(false);
    }
  };

  // Default placeholder image if group has no image
  const getImageUrl = (group) => {
    if (group.imageUrl && group.imageUrl.trim() !== '') {
      return group.imageUrl;
    }
    
    // Default images based on cuisine type
    const defaultImages = {
      'Italian': 'https://images.unsplash.com/photo-1498579150354-977475b7ea0b?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Mexican': 'https://images.unsplash.com/photo-1599974579688-8dbdd335c77f?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Chinese': 'https://images.unsplash.com/photo-1563245372-f21724e3856d?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Indian': 'https://images.unsplash.com/photo-1585937421612-70a008356c36?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Japanese': 'https://images.unsplash.com/photo-1553621042-f6e147245754?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Thai': 'https://images.unsplash.com/photo-1559847844-5315695dadae?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      'Mediterranean': 'https://images.unsplash.com/photo-1556804335-2fa741d43774?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60'
    };
    
    return defaultImages[group.cuisineType] || 'https://images.unsplash.com/photo-1476718406336-bb5a9690ee2a?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60';
  };

  return (
    <div className="group-list-container">
      <div className="group-list-header">
        <h2>Cooking Community Groups</h2>
        <p className="header-subtitle">Connect with fellow food enthusiasts and share your culinary journey</p>
        <Link to="/groups/create" className="create-group-btn">
          <span className="btn-icon">+</span>
          Create New Group
        </Link>
      </div>
      
      <div className="group-list-filters">
        <button 
          className={`filter-btn ${currentFilter === 'all' ? 'active' : ''}`}
          onClick={() => setCurrentFilter('all')}
        >
          All Groups
        </button>
        <button 
          className={`filter-btn ${currentFilter === 'member' ? 'active' : ''}`}
          onClick={() => setCurrentFilter('member')}
        >
          My Groups
        </button>
        <button 
          className={`filter-btn ${currentFilter === 'admin' ? 'active' : ''}`}
          onClick={() => setCurrentFilter('admin')}
        >
          Created by Me
        </button>
      </div>
      
      <div className="group-search">
        <form onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Search by name or cuisine type..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button type="submit">
            <i className="search-icon">üîç</i>
            Search
          </button>
        </form>
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {loading ? (
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading delicious groups...</p>
        </div>
      ) : groups.length === 0 ? (
        <div className="no-groups">
          <p>No groups found. {currentFilter === 'all' ? 'Be the first to create a cooking community!' : ''}</p>
          <Link to="/groups/create" className="empty-create-btn">Create a New Group</Link>
        </div>
      ) : (
        <div className="group-cards">
          {groups.map(group => (
            <div key={group.id} className="group-card">
              <div className="group-card-image" style={{ backgroundImage: `url(${getImageUrl(group)})` }}>
                <div className="cuisine-badge">{group.cuisineType}</div>
              </div>
              <div className="group-card-content">
                <h3>{group.name}</h3>
                <p className="description">{group.description.length > 120 ? 
                  `${group.description.substring(0, 120)}...` : 
                  group.description}
                </p>
                <div className="group-meta">
                  <span className="member-count"><i className="member-icon">üë•</i> {group.memberCount || 1} member{(group.memberCount || 1) !== 1 ? 's' : ''}</span>
                  <Link to={`/groups/${group.id}`} className="view-group-btn">View Group</Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default GroupList;
