import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import RecipeService from '../services/RecipeService';
import { UserContext } from './UserContext';
import Navbar from '../components/Navbar';
import '../styles/Recipes.css';

const Recipes = () => {
    const { user } = useContext(UserContext);
    const [recipes, setRecipes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState('');
    const [filters, setFilters] = useState({
        cuisine: '',
        dietary: '',
        ingredient: ''
    });
    const [cuisineOptions] = useState([
        'Italian', 'Mexican', 'Chinese', 'Indian', 'Japanese', 
        'Thai', 'French', 'Mediterranean', 'American', 'Other'
    ]);
    const [dietaryOptions] = useState([
        'Vegetarian', 'Vegan', 'Gluten-Free', 'Dairy-Free', 
        'Keto', 'Low-Carb', 'Paleo', 'None'
    ]);

    useEffect(() => {
        fetchRecipes();
    }, []);

    const fetchRecipes = async () => {
        try {
            const response = await RecipeService.getAllRecipes();
            setRecipes(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching recipes:', error);
            setLoading(false);
        }
    };

    const handleSearch = async (e) => {
        e.preventDefault();
        if (searchQuery.trim()) {
            try {
                const response = await RecipeService.searchRecipes(searchQuery);
                setRecipes(response.data);
            } catch (error) {
                console.error('Error searching recipes:', error);
            }
        } else {
            fetchRecipes();
        }
    };

    const handleFilter = async (e) => {
        e.preventDefault();
        try {
            const response = await RecipeService.filterRecipes(
                filters.cuisine || null,
                filters.dietary || null,
                filters.ingredient || null
            );
            setRecipes(response.data);
        } catch (error) {
            console.error('Error filtering recipes:', error);
        }
    };

    const resetFilters = () => {
        setFilters({
            cuisine: '',
            dietary: '',
            ingredient: ''
        });
        fetchRecipes();
    };

    return (
        <div className="recipes-container">
            <Navbar />
            <div className="recipes-header">
                <h1>Explore Recipes</h1>
                {user && (
                    <Link to="/recipes/create" className="create-recipe-btn">
                        Create New Recipe
                    </Link>
                )}
            </div>

            <div className="search-filter-container">
                <div className="search-container">
                    <form onSubmit={handleSearch}>
                        <input
                            type="text"
                            placeholder="Search recipes..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                        <button type="submit">Search</button>
                    </form>
                </div>

                <div className="filter-container">
                    <h3>Filter Recipes</h3>
                    <form onSubmit={handleFilter}>
                        <div className="filter-group">
                            <label>Cuisine:</label>
                            <select
                                value={filters.cuisine}
                                onChange={(e) => setFilters({...filters, cuisine: e.target.value})}
                            >
                                <option value="">All Cuisines</option>
                                {cuisineOptions.map((cuisine, index) => (
                                    <option key={index} value={cuisine}>{cuisine}</option>
                                ))}
                            </select>
                        </div>

                        <div className="filter-group">
                            <label>Dietary Preference:</label>
                            <select
                                value={filters.dietary}
                                onChange={(e) => setFilters({...filters, dietary: e.target.value})}
                            >
                                <option value="">All Preferences</option>
                                {dietaryOptions.map((diet, index) => (
                                    <option key={index} value={diet}>{diet}</option>
                                ))}
                            </select>
                        </div>

                        <div className="filter-group">
                            <label>Ingredient:</label>
                            <input
                                type="text"
                                placeholder="Enter ingredient"
                                value={filters.ingredient}
                                onChange={(e) => setFilters({...filters, ingredient: e.target.value})}
                            />
                        </div>

                        <div className="filter-buttons">
                            <button type="submit">Apply Filters</button>
                            <button type="button" onClick={resetFilters}>Reset</button>
                        </div>
                    </form>
                </div>
            </div>

            {loading ? (
                <div className="loading">Loading recipes...</div>
            ) : (
                <div className="recipes-grid">
                    {recipes.length > 0 ? (
                        recipes.map((recipe) => (
                            <div key={recipe.id} className="recipe-card">
                                <div className="recipe-image">
                                    <img 
                                        src={recipe.imageUrl || 'https://via.placeholder.com/300x200?text=No+Image'} 
                                        alt={recipe.title} 
                                    />
                                </div>
                                <div className="recipe-info">
                                    <h3>{recipe.title}</h3>
                                    <p className="recipe-cuisine">{recipe.cuisine}</p>
                                    <p className="recipe-dietary">{recipe.dietaryPreferences}</p>
                                    <p className="recipe-author">By: {recipe.userName}</p>
                                    <Link to={`/recipes/${recipe.id}`} className="view-recipe-btn">
                                        View Recipe
                                    </Link>
                                </div>
                            </div>
                        ))
                    ) : (
                        <div className="no-recipes">No recipes found.</div>
                    )}
                </div>
            )}
        </div>
    );
};

export default Recipes;
