import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RecipeService from '../services/RecipeService';
import { useUser } from './UserContext';

const MyRecipes = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useUser();
  const navigate = useNavigate();
  
  useEffect(() => {
    if (!user || !user.id) {
      navigate('/');
      return;
    }
    
    fetchMyRecipes();
  }, [user, navigate]);
  
  const fetchMyRecipes = () => {
    setLoading(true);
    RecipeService.getRecipesByUser(user.id)
      .then(response => {
        setRecipes(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching recipes:', error);
        setLoading(false);
      });
  };
  
  const handleEditRecipe = (id) => {
    navigate(`/recipes/${id}/edit`);
  };
  
  const handleDeleteRecipe = (id) => {
    if (window.confirm('Are you sure you want to delete this recipe? This action cannot be undone.')) {
      setLoading(true);
      RecipeService.deleteRecipe(id, user.id)
        .then(() => {
          fetchMyRecipes();
        })
        .catch(error => {
          console.error('Error deleting recipe:', error);
          setLoading(false);
          alert('Failed to delete recipe. Please try again.');
        });
    }
  };
  
  return (
    <div className="my-recipes-container">
      <div className="my-recipes-header">
        <h1>My Recipes</h1>
        <div className="action-buttons">
          <button 
            className="create-recipe-btn"
            onClick={() => navigate('/recipes/create')}
          >
            Create New Recipe
          </button>
          <button 
            className="home-btn"
            onClick={() => navigate('/home')}
          >
            Back to Home
          </button>
        </div>
      </div>
      
      {loading ? (
        <div className="loading">Loading recipes...</div>
      ) : recipes.length > 0 ? (
        <div className="recipes-grid">
          {recipes.map(recipe => (
            <div key={recipe.id} className="recipe-card">
              <div 
                className="recipe-content"
                onClick={() => navigate(`/recipes/${recipe.id}`)}
              >
                <div className="recipe-image-container">
                  <img 
                    src={recipe.imageUrl || 'https://via.placeholder.com/300x200?text=No+Image'} 
                    alt={recipe.title} 
                    className="recipe-image" 
                  />
                </div>
                <div className="recipe-info">
                  <h3>{recipe.title}</h3>
                  <p className="recipe-description">{recipe.description}</p>
                  <div className="recipe-meta">
                    <span className="cuisine-type">{recipe.cuisineType}</span>
                    <span className="comments-count">{recipe.commentsCount} comments</span>
                  </div>
                  <span className="created-date">
                    Created: {new Date(recipe.createdAt).toLocaleDateString()}
                  </span>
                </div>
              </div>
              
              <div className="recipe-actions">
                <button 
                  className="edit-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleEditRecipe(recipe.id);
                  }}
                >
                  Edit
                </button>
                <button 
                  className="delete-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDeleteRecipe(recipe.id);
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-recipes">
          <p>You haven't created any recipes yet.</p>
          <button onClick={() => navigate('/recipes/create')}>Create Your First Recipe</button>
        </div>
      )}
    </div>
  );
};

export default MyRecipes;
