import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import RecipeService from '../../services/RecipeService';
import '../../styles/RecipeList.css';

const RecipeList = ({ recipes, userRole = 'guest', currentUserId, onDeleteRecipe, showControls = true }) => {
  const navigate = useNavigate();

  const handleDelete = async (recipeId) => {
    if (window.confirm('Are you sure you want to delete this recipe?')) {
      try {
        await RecipeService.deleteRecipe(recipeId, currentUserId);
        if (onDeleteRecipe) {
          onDeleteRecipe(recipeId);
        }
      } catch (error) {
        console.error('Error deleting recipe:', error);
        alert('Failed to delete recipe. Please try again.');
      }
    }
  };

  if (!recipes || recipes.length === 0) {
    return <p className="no-recipes">No recipes found.</p>;
  }

  return (
    <div className="recipe-list">
      {recipes.map(recipe => (
        <div key={recipe.id} className="recipe-card">
          <div className="recipe-image-container">
            {recipe.imageUrl ? (
              <img 
                src={recipe.imageUrl} 
                alt={recipe.title} 
                className="recipe-image" 
              />
            ) : (
              <div className="recipe-image-placeholder">
                <span>No Image</span>
              </div>
            )}
          </div>
          <div className="recipe-details">
            <h3 className="recipe-title">{recipe.title}</h3>
            <p className="recipe-cuisine">{recipe.cuisineType || 'Uncategorized'}</p>
            <p className="recipe-meta">
              <span className="prep-time">Prep: {recipe.prepTime || '-'} min</span>
              <span className="cook-time">Cook: {recipe.cookTime || '-'} min</span>
            </p>
            <p className="recipe-author">By: {recipe.userName}</p>
            
            <div className="recipe-tags">
              {recipe.dietaryPreferences && recipe.dietaryPreferences.split(',').map((pref, index) => (
                <span key={index} className="recipe-tag">{pref.trim()}</span>
              ))}
            </div>
            
            <div className="recipe-actions">
              <Link to={`/recipes/${recipe.id}`} className="btn btn-view">View Recipe</Link>
              
              {showControls && currentUserId && recipe.userId === parseInt(currentUserId) && (
                <>
                  <Link to={`/recipes/edit/${recipe.id}`} className="btn btn-edit">Edit</Link>
                  <button 
                    onClick={() => handleDelete(recipe.id)} 
                    className="btn btn-delete"
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default RecipeList;
