import React, { useState, useEffect, useRef } from 'react';
import GroupService from '../services/GroupService';
import { useUser } from '../pages/UserContext';
import '../styles/GroupMessages.css';
import Swal from 'sweetalert2';

const GroupMessages = ({ groupId, userRole }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);
  const { user } = useUser();
  
  // Get current user ID from context
  const currentUserId = user ? user.id : null;

  // Check if the current user is a member of the group
  const canPostMessage = userRole.isMember && currentUserId;

  useEffect(() => {
    if (currentUserId) {
      loadMessages();
      
      // Set up polling for new messages every 10 seconds
      const intervalId = setInterval(loadMessages, 10000);
      
      return () => clearInterval(intervalId);
    }
  }, [groupId, currentUserId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const loadMessages = async () => {
    try {
      const response = await GroupService.getGroupMessages(groupId, currentUserId);
      setMessages(response.data);
      setError(null);
    } catch (err) {
      console.error('Error loading messages:', err);
      setError('Failed to load messages. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    
    if (!newMessage.trim()) return;
    
    try {
      await GroupService.createMessage(groupId, newMessage, currentUserId);
      setNewMessage('');
      loadMessages(); // Reload messages to show the new one
    } catch (err) {
      console.error('Error sending message:', err);
      Swal.fire({
        icon: 'error',
        title: 'Failed to Send',
        text: 'There was an error sending your message'
      });
    }
  };

  const handleDeleteMessage = async (messageId) => {
    try {
      const result = await Swal.fire({
        icon: 'warning',
        title: 'Delete Message?',
        text: 'Are you sure you want to delete this message?',
        showCancelButton: true,
        confirmButtonText: 'Yes, Delete',
        cancelButtonText: 'Cancel'
      });
      
      if (result.isConfirmed) {
        await GroupService.deleteMessage(groupId, messageId, currentUserId);
        loadMessages(); // Reload messages to reflect the deletion
      }
    } catch (err) {
      console.error('Error deleting message:', err);
      Swal.fire({
        icon: 'error',
        title: 'Failed to Delete',
        text: 'There was an error deleting the message'
      });
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(date);
  };

  if (loading) {
    return <div className="messages-loading">Loading messages...</div>;
  }

  if (error) {
    return <div className="messages-error">{error}</div>;
  }

  const canDeleteMessage = (message) => {
    return message.userId === currentUserId || 
           userRole.isModerator || 
           userRole.isAdmin;
  };

  return (
    <div className="group-messages-container">
      <h3>Group Messages</h3>
      
      <div className="messages-list">
        {messages.length === 0 ? (
          <div className="no-messages">No messages yet. Start the conversation!</div>
        ) : (
          messages.map(message => (
            <div key={message.id} className={`message-item ${message.userId === currentUserId ? 'own-message' : ''}`}>
              <div className="message-header">
                <span className="message-author">{message.userName}</span>
                <span className="message-time">{formatDate(message.createdAt)}</span>
                {canDeleteMessage(message) && (
                  <button 
                    className="delete-message-btn" 
                    onClick={() => handleDeleteMessage(message.id)}
                    title="Delete message"
                  >
                    &times;
                  </button>
                )}
              </div>
              <div className="message-content">{message.content}</div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>
      
      {canPostMessage ? (
        <form className="message-form" onSubmit={handleSendMessage}>
          <textarea
            className="message-input"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type your message here..."
            rows={3}
          />
          <button 
            type="submit" 
            className="send-btn"
            disabled={!newMessage.trim()}
          >
            Send
          </button>
        </form>
      ) : (
        <div className="join-message">
          <p>You need to join this group to participate in the conversation.</p>
        </div>
      )}
    </div>
  );
};

export default GroupMessages;
