import React, { useState, useEffect } from 'react';
import { useUser } from './UserContext';
import { Link, useNavigate } from 'react-router-dom';
import RecipeService from '../services/RecipeService';
import RecipeList from '../components/recipe/RecipeList';
import '../styles/RecipesPage.css';

const RecipesPage = () => {
  const { user } = useUser();
  const navigate = useNavigate();
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchParams, setSearchParams] = useState({
    title: '',
    cuisine: '',
    diet: '',
    ingredient: ''
  });
  const [activeTab, setActiveTab] = useState('all');

  useEffect(() => {
    fetchRecipes();
  }, [activeTab]);

  const fetchRecipes = async () => {
    setLoading(true);
    setError(null);
    try {
      let response;
      if (activeTab === 'all') {
        response = await RecipeService.getAllRecipes();
      } else if (activeTab === 'my') {
        if (!user || !user.id) {
          navigate('/');
          return;
        }
        response = await RecipeService.getRecipesByUser(user.id);
      }
      setRecipes(response.data);
    } catch (err) {
      console.error('Error fetching recipes:', err);
      setError('Failed to load recipes. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      // Filter out empty parameters
      const params = Object.entries(searchParams)
        .filter(([_, value]) => value.trim() !== '')
        .reduce((obj, [key, value]) => ({ ...obj, [key]: value }), {});

      const response = await RecipeService.searchRecipes(params);
      setRecipes(response.data);
    } catch (err) {
      console.error('Error searching recipes:', err);
      setError('Failed to search recipes. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams({ ...searchParams, [name]: value });
  };

  const handleDeleteRecipe = (deletedRecipeId) => {
    setRecipes(recipes.filter(recipe => recipe.id !== deletedRecipeId));
  };

  const clearSearch = () => {
    setSearchParams({
      title: '',
      cuisine: '',
      diet: '',
      ingredient: ''
    });
    fetchRecipes();
  };

  return (
    <div className="recipes-page">
      <div className="recipes-header">
        <h1>Recipe Management</h1>
        <Link to="/recipes/create" className="btn btn-create">Add New Recipe</Link>
      </div>

      <div className="recipes-tabs">
        <button 
          className={`tab ${activeTab === 'all' ? 'active' : ''}`}
          onClick={() => setActiveTab('all')}
        >
          All Recipes
        </button>
        {user && (
          <button 
            className={`tab ${activeTab === 'my' ? 'active' : ''}`}
            onClick={() => setActiveTab('my')}
          >
            My Recipes
          </button>
        )}
      </div>

      <div className="search-container">
        <form onSubmit={handleSearch} className="search-form">
          <div className="search-row">
            <div className="search-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                id="title"
                name="title"
                value={searchParams.title}
                onChange={handleInputChange}
                placeholder="Search by title"
              />
            </div>
            
            <div className="search-group">
              <label htmlFor="cuisine">Cuisine</label>
              <input
                type="text"
                id="cuisine"
                name="cuisine"
                value={searchParams.cuisine}
                onChange={handleInputChange}
                placeholder="E.g. Italian, Mexican"
              />
            </div>
          </div>
          
          <div className="search-row">
            <div className="search-group">
              <label htmlFor="diet">Dietary Preference</label>
              <input
                type="text"
                id="diet"
                name="diet"
                value={searchParams.diet}
                onChange={handleInputChange}
                placeholder="E.g. Vegetarian, Vegan"
              />
            </div>
            
            <div className="search-group">
              <label htmlFor="ingredient">Ingredient</label>
              <input
                type="text"
                id="ingredient"
                name="ingredient"
                value={searchParams.ingredient}
                onChange={handleInputChange}
                placeholder="Search by ingredient"
              />
            </div>
          </div>
          
          <div className="search-actions">
            <button type="submit" className="btn btn-search">Search</button>
            <button type="button" onClick={clearSearch} className="btn btn-clear">Clear</button>
          </div>
        </form>
      </div>

      {loading ? (
        <div className="loading">Loading recipes...</div>
      ) : error ? (
        <div className="error">{error}</div>
      ) : (
        <RecipeList 
          recipes={recipes} 
          currentUserId={user?.id} 
          onDeleteRecipe={handleDeleteRecipe}
        />
      )}
    </div>
  );
};

export default RecipesPage;
