import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import RecipeService from '../services/RecipeService';
import { useUser } from './UserContext';
import Navbar from '../components/Navbar';
import '../styles/CreateRecipe.css';

const CreateRecipe = () => {
    const { user } = useUser();
    const navigate = useNavigate();
    
    const [cuisineOptions] = useState([
        'Italian', 'Mexican', 'Chinese', 'Indian', 'Japanese', 
        'Thai', 'French', 'Mediterranean', 'American', 'Other'
    ]);
    
    const [dietaryOptions] = useState([
        'Vegetarian', 'Vegan', 'Gluten-Free', 'Dairy-Free', 
        'Keto', 'Low-Carb', 'Paleo', 'None'
    ]);
    
    const [recipe, setRecipe] = useState({
        title: '',
        description: '',
        instructions: '',
        imageUrl: '',
        cuisine: '',
        dietaryPreferences: '',
        ingredients: [{ name: '', quantity: '', unit: '' }]
    });
    
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    
    // If user not logged in, redirect to login
    if (!user) {
        navigate('/');
        return null;
    }
    
    const handleChange = (e) => {
        const { name, value } = e.target;
        setRecipe(prev => ({
            ...prev,
            [name]: value
        }));
    };
    
    const handleIngredientChange = (index, e) => {
        const { name, value } = e.target;
        const updatedIngredients = [...recipe.ingredients];
        updatedIngredients[index] = {
            ...updatedIngredients[index],
            [name]: value
        };
        
        setRecipe(prev => ({
            ...prev,
            ingredients: updatedIngredients
        }));
    };
    
    const addIngredient = () => {
        setRecipe(prev => ({
            ...prev,
            ingredients: [...prev.ingredients, { name: '', quantity: '', unit: '' }]
        }));
    };
    
    const removeIngredient = (index) => {
        if (recipe.ingredients.length === 1) return;
        
        const updatedIngredients = recipe.ingredients.filter((_, i) => i !== index);
        setRecipe(prev => ({
            ...prev,
            ingredients: updatedIngredients
        }));
    };
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Validate recipe
        if (!recipe.title || !recipe.description || !recipe.instructions) {
            setError('Please fill in all required fields');
            return;
        }
        
        // Validate ingredients
        const invalidIngredients = recipe.ingredients.some(
            ing => !ing.name || !ing.quantity
        );
        
        if (invalidIngredients) {
            setError('Please fill in all ingredient fields');
            return;
        }
        
        setLoading(true);
        setError('');
        
        try {
            // Remove any empty fields before submitting
            const cleanedIngredients = recipe.ingredients.map(ing => ({
                name: ing.name.trim(),
                quantity: ing.quantity.trim(),
                unit: ing.unit ? ing.unit.trim() : ""
            }));
            
            // Ensure all fields are properly formatted
            const recipePayload = {
                title: recipe.title.trim(),
                description: recipe.description.trim(),
                instructions: recipe.instructions.trim(),
                imageUrl: recipe.imageUrl ? recipe.imageUrl.trim() : "",
                cuisine: recipe.cuisine || "Other",
                dietaryPreferences: recipe.dietaryPreferences || "None",
                ingredients: cleanedIngredients
            };
            
            console.log('Submitting recipe payload:', JSON.stringify(recipePayload));
            
            // Check if user exists and has valid ID
            if (!user || !user.id) {
                setError('User authentication issue. Please try logging in again.');
                setLoading(false);
                return;
            }
            
            console.log('User ID for submission:', user.id);
            
            const response = await RecipeService.createRecipe(recipePayload, user.id);
            console.log('Recipe created successfully:', response.data);
            navigate('/recipes');
        } catch (err) {
            console.error('Error creating recipe:', err);
            
            // More detailed error information
            if (err.response) {
                console.error('Error response data:', err.response.data);
                console.error('Error response status:', err.response.status);
                console.error('Error response headers:', err.response.headers);
                
                if (err.response.data && err.response.data.message) {
                    setError(`Server error: ${err.response.data.message}`);
                } else {
                    setError(`Server error: ${err.response.status} - ${err.response.statusText}`);
                }
            } else if (err.request) {
                // The request was made but no response received
                setError('No response from server. Please check your connection and try again.');
            } else {
                // Something else caused the error
                setError('Failed to create recipe: ' + err.message);
            }
            setLoading(false);
        }
    };
    
    // Add a debug function to test connection to the server
    const testConnection = async () => {
        try {
            // Create a simple test recipe object
            const testRecipe = {
                title: "Test Recipe",
                description: "Test description",
                instructions: "Test instructions",
                ingredients: [{ name: "Test ingredient", quantity: "1", unit: "piece" }]
            };
            
            console.log('Testing server connection with test recipe');
            const response = await RecipeService.debugCreateRecipe(testRecipe, user.id);
            console.log('Debug test successful:', response.data);
            setError('Debug test successful! See console for details.');
        } catch (err) {
            console.error('Debug test failed:', err);
            setError(`Debug test failed: ${err.message}`);
        }
    };
    
    return (
        <div className="create-recipe-container">
            <Navbar />
            <div className="create-recipe-content">
                <h2>Create New Recipe</h2>
                
                {error && <div className="error-message">{error}</div>}
                
                <form onSubmit={handleSubmit} className="recipe-form">
                    <div className="form-group">
                        <label htmlFor="title">Title*</label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={recipe.title}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    
                    <div className="form-group">
                        <label htmlFor="description">Description*</label>
                        <textarea
                            id="description"
                            name="description"
                            value={recipe.description}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    
                    <div className="form-group">
                        <label htmlFor="imageUrl">Image URL</label>
                        <input
                            type="url"
                            id="imageUrl"
                            name="imageUrl"
                            value={recipe.imageUrl}
                            onChange={handleChange}
                            placeholder="https://example.com/image.jpg"
                        />
                        {recipe.imageUrl && (
                            <div className="image-preview">
                                <img src={recipe.imageUrl} alt="Recipe preview" />
                            </div>
                        )}
                    </div>
                    
                    <div className="form-row">
                        <div className="form-group">
                            <label htmlFor="cuisine">Cuisine</label>
                            <select
                                id="cuisine"
                                name="cuisine"
                                value={recipe.cuisine}
                                onChange={handleChange}
                            >
                                <option value="">Select Cuisine</option>
                                {cuisineOptions.map((cuisine, index) => (
                                    <option key={index} value={cuisine}>{cuisine}</option>
                                ))}
                            </select>
                        </div>
                        
                        <div className="form-group">
                            <label htmlFor="dietaryPreferences">Dietary Preferences</label>
                            <select
                                id="dietaryPreferences"
                                name="dietaryPreferences"
                                value={recipe.dietaryPreferences}
                                onChange={handleChange}
                            >
                                <option value="">Select Preference</option>
                                {dietaryOptions.map((diet, index) => (
                                    <option key={index} value={diet}>{diet}</option>
                                ))}
                            </select>
                        </div>
                    </div>
                    
                    <div className="form-group ingredients-section">
                        <label>Ingredients*</label>
                        {recipe.ingredients.map((ingredient, index) => (
                            <div key={index} className="ingredient-row">
                                <div className="ingredient-field">
                                    <input
                                        type="text"
                                        name="name"
                                        placeholder="Ingredient name"
                                        value={ingredient.name}
                                        onChange={(e) => handleIngredientChange(index, e)}
                                        required
                                    />
                                </div>
                                <div className="ingredient-field">
                                    <input
                                        type="text"
                                        name="quantity"
                                        placeholder="Quantity"
                                        value={ingredient.quantity}
                                        onChange={(e) => handleIngredientChange(index, e)}
                                        required
                                    />
                                </div>
                                <div className="ingredient-field">
                                    <input
                                        type="text"
                                        name="unit"
                                        placeholder="Unit (e.g., g, ml, tsp)"
                                        value={ingredient.unit}
                                        onChange={(e) => handleIngredientChange(index, e)}
                                    />
                                </div>
                                <button 
                                    type="button" 
                                    className="remove-ingredient-btn"
                                    onClick={() => removeIngredient(index)}
                                    disabled={recipe.ingredients.length === 1}
                                >
                                    âœ•
                                </button>
                            </div>
                        ))}
                        <button 
                            type="button" 
                            className="add-ingredient-btn"
                            onClick={addIngredient}
                        >
                            + Add Ingredient
                        </button>
                    </div>
                    
                    <div className="form-group">
                        <label htmlFor="instructions">Instructions*</label>
                        <textarea
                            id="instructions"
                            name="instructions"
                            value={recipe.instructions}
                            onChange={handleChange}
                            rows="10"
                            required
                            placeholder="Enter step-by-step instructions..."
                        />
                    </div>
                    
                    <div className="form-actions">
                        <button 
                            type="button" 
                            className="cancel-btn"
                            onClick={() => navigate('/recipes')}
                        >
                            Cancel
                        </button>
                        <button 
                            type="button"
                            className="debug-btn"
                            onClick={testConnection}
                        >
                            Test Connection
                        </button>
                        <button 
                            type="submit" 
                            className="submit-btn"
                            disabled={loading}
                        >
                            {loading ? 'Creating...' : 'Create Recipe'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default CreateRecipe;
