import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import RecipeService from '../services/RecipeService';
import { UserContext } from './UserContext';

const RecipeDetail = () => {
  const { recipeId } = useParams();
  const navigate = useNavigate();
  const { user } = useContext(UserContext);
  
  const [recipe, setRecipe] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [loading, setLoading] = useState(true);
  const [commentsLoading, setCommentsLoading] = useState(true);
  const [isOwner, setIsOwner] = useState(false);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    fetchRecipeData();
    fetchComments();
  }, [recipeId]);
  
  const fetchRecipeData = () => {
    setLoading(true);
    RecipeService.getRecipeById(recipeId)
      .then(response => {
        setRecipe(response.data);
        setIsOwner(user && user.id === response.data.userId);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching recipe:', error);
        setError('Recipe not found or could not be loaded.');
        setLoading(false);
      });
  };
  
  const fetchComments = () => {
    setCommentsLoading(true);
    RecipeService.getCommentsByRecipe(recipeId)
      .then(response => {
        setComments(response.data);
        setCommentsLoading(false);
      })
      .catch(error => {
        console.error('Error fetching comments:', error);
        setCommentsLoading(false);
      });
  };
  
  const handleCommentSubmit = (e) => {
    e.preventDefault();
    
    if (!user) {
      alert('You must be logged in to post a comment');
      navigate('/');
      return;
    }
    
    if (!newComment.trim()) {
      return;
    }
    
    const commentData = {
      content: newComment,
      recipeId: parseInt(recipeId)
    };
    
    RecipeService.createComment(commentData, user.id)
      .then(() => {
        setNewComment('');
        fetchComments();
      })
      .catch(error => {
        console.error('Error posting comment:', error);
        alert('Failed to post comment. Please try again.');
      });
  };
  
  const handleDeleteComment = (commentId) => {
    if (window.confirm('Are you sure you want to delete this comment?')) {
      RecipeService.deleteComment(commentId, user.id)
        .then(() => {
          fetchComments();
        })
        .catch(error => {
          console.error('Error deleting comment:', error);
          alert('Failed to delete comment. Please try again.');
        });
    }
  };
  
  const handleEditRecipe = () => {
    navigate(`/recipes/${recipeId}/edit`);
  };
  
  const handleDeleteRecipe = () => {
    if (window.confirm('Are you sure you want to delete this recipe? This action cannot be undone.')) {
      RecipeService.deleteRecipe(recipeId, user.id)
        .then(() => {
          navigate('/recipes');
        })
        .catch(error => {
          console.error('Error deleting recipe:', error);
          alert('Failed to delete recipe. Please try again.');
        });
    }
  };
  
  if (loading) {
    return <div className="loading">Loading recipe...</div>;
  }
  
  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => navigate('/recipes')}>Back to Recipes</button>
      </div>
    );
  }
  
  return (
    <div className="recipe-detail-container">
      <div className="recipe-detail-header">
        <button onClick={() => navigate('/recipes')} className="back-btn">
          Back to Recipes
        </button>
        {isOwner && (
          <div className="owner-actions">
            <button onClick={handleEditRecipe} className="edit-btn">
              Edit Recipe
            </button>
            <button onClick={handleDeleteRecipe} className="delete-btn">
              Delete Recipe
            </button>
          </div>
        )}
      </div>
      
      <div className="recipe-content">
        <h1 className="recipe-title">{recipe.title}</h1>
        
        <div className="recipe-meta">
          <span className="cuisine-type">Cuisine: {recipe.cuisineType}</span>
          <span className="recipe-author">By: {recipe.userName}</span>
          <span className="recipe-date">Created: {new Date(recipe.createdAt).toLocaleDateString()}</span>
          {recipe.updatedAt !== recipe.createdAt && (
            <span className="recipe-updated">Updated: {new Date(recipe.updatedAt).toLocaleDateString()}</span>
          )}
        </div>
        
        {recipe.dietaryPreferences && recipe.dietaryPreferences.length > 0 && (
          <div className="dietary-tags">
            {recipe.dietaryPreferences.map(pref => (
              <span key={pref} className="dietary-tag">{pref}</span>
            ))}
          </div>
        )}
        
        <div className="recipe-image-container">
          <img 
            src={recipe.imageUrl || 'https://via.placeholder.com/800x400?text=No+Image'} 
            alt={recipe.title} 
            className="recipe-image" 
          />
        </div>
        
        <div className="recipe-description">
          <h2>Description</h2>
          <p>{recipe.description}</p>
        </div>
        
        <div className="recipe-ingredients">
          <h2>Ingredients</h2>
          <ul>
            {recipe.ingredients.map((ingredient, index) => (
              <li key={index}>
                <span className="ingredient-quantity">{ingredient.quantity}</span>
                <span className="ingredient-name">{ingredient.name}</span>
              </li>
            ))}
          </ul>
        </div>
        
        <div className="recipe-instructions">
          <h2>Instructions</h2>
          <div className="instructions-text">
            {recipe.instructions.split('\n').map((paragraph, index) => (
              <p key={index}>{paragraph}</p>
            ))}
          </div>
        </div>
      </div>
      
      <div className="comments-section">
        <h2>Comments ({comments.length})</h2>
        
        {user ? (
          <form onSubmit={handleCommentSubmit} className="comment-form">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Share your thoughts or tips about this recipe..."
              rows="3"
              required
            />
            <button type="submit">Post Comment</button>
          </form>
        ) : (
          <div className="login-prompt">
            <p>Please <a href="/">log in</a> to leave a comment.</p>
          </div>
        )}
        
        <div className="comments-list">
          {commentsLoading ? (
            <p>Loading comments...</p>
          ) : comments.length > 0 ? (
            comments.map(comment => (
              <div key={comment.id} className="comment">
                <div className="comment-header">
                  <span className="comment-author">{comment.userName}</span>
                  <span className="comment-date">{new Date(comment.createdAt).toLocaleString()}</span>
                </div>
                <p className="comment-content">{comment.content}</p>
                {user && user.id === comment.userId && (
                  <button 
                    className="delete-comment-btn"
                    onClick={() => handleDeleteComment(comment.id)}
                  >
                    Delete
                  </button>
                )}
              </div>
            ))
          ) : (
            <p className="no-comments">No comments yet. Be the first to share your thoughts!</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default RecipeDetail;
