import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import GroupService from '../services/GroupService';
import GroupNav from './GroupNav';
import { useUser } from '../pages/UserContext';
import Swal from 'sweetalert2';
import '../styles/GroupList.css';

const MyGroups = () => {
  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const { user } = useUser();
  
  // Get current user ID from context
  const currentUserId = user ? user.id : null;

  useEffect(() => {
    if (currentUserId) {
      loadGroups();
    }
  }, [currentUserId]);

  const loadGroups = async () => {
    setLoading(true);
    try {
      const response = await GroupService.getGroupsByMember(currentUserId);
      setGroups(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to load your groups. Please try again later.');
      console.error('Error loading groups:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleLeaveGroup = async (groupId) => {
    try {
      const result = await Swal.fire({
        icon: 'warning',
        title: 'Leave Group?',
        text: 'Are you sure you want to leave this group?',
        showCancelButton: true,
        confirmButtonText: 'Yes, Leave',
        cancelButtonText: 'Cancel'
      });
      
      if (result.isConfirmed) {
        await GroupService.leaveGroup(groupId, currentUserId);
        Swal.fire({
          icon: 'success',
          title: 'Left Successfully',
          text: 'You have left the group'
        });
        loadGroups(); // Refresh the list
      }
    } catch (err) {
      Swal.fire({
        icon: 'error',
        title: 'Failed to Leave',
        text: err.response?.data?.message || 'There was an error leaving the group'
      });
      console.error('Error leaving group:', err);
    }
  };

  return (
    <div className="group-list-container">
      <GroupNav currentPage="member" />
      
      <h2 className="page-title">My Groups</h2>
      <p className="page-description">
        Groups you've joined. Participate in discussions, share recipes, and connect with like-minded food enthusiasts.
      </p>
      
      {error && <div className="error-message">{error}</div>}
      
      {loading ? (
        <div className="loading">Loading your groups...</div>
      ) : groups.length === 0 ? (
        <div className="no-groups">
          <p>You haven't joined any groups yet.</p>
          <button 
            className="primary-btn"
            onClick={() => navigate('/groups')}
          >
            Find Groups to Join
          </button>
        </div>
      ) : (
        <div className="group-cards">
          {groups.map(group => (
            <div key={group.id} className="group-card">
              <div className="group-card-image">
                {group.imageUrl ? (
                  <img src={group.imageUrl} alt={group.name} />
                ) : (
                  <div className="group-card-image-placeholder">
                    <i className="fa fa-cutlery"></i>
                  </div>
                )}
              </div>
              <div className="group-card-content">
                <h3>{group.name}</h3>
                <p className="cuisine-type">Cuisine: {group.cuisineType}</p>
                <p className="description">{group.description}</p>
                <div className="group-meta">
                  <span><i className="fa fa-users"></i> {group.memberCount} members</span>
                  <span><i className="fa fa-user"></i> {group.adminName}</span>
                </div>
                <div className="group-card-actions">
                  <button 
                    className="view-btn"
                    onClick={() => navigate(`/groups/${group.id}`)}
                  >
                    View Group
                  </button>
                  
                  {group.adminId !== currentUserId && (
                    <button 
                      className="leave-btn"
                      onClick={() => handleLeaveGroup(group.id)}
                    >
                      Leave Group
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyGroups;
