import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { useUser } from './UserContext';
import RecipeService from '../services/RecipeService';
import '../styles/RecipeDetail.css';

const RecipeDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useUser();
  const [recipe, setRecipe] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isOwner, setIsOwner] = useState(false);

  useEffect(() => {
    const fetchRecipeAndComments = async () => {
      setLoading(true);
      setError(null);
      try {
        // Fetch recipe details
        const recipeResponse = await RecipeService.getRecipeById(id);
        setRecipe(recipeResponse.data);

        // Fetch recipe comments
        const commentsResponse = await RecipeService.getRecipeComments(id);
        setComments(commentsResponse.data);

        // Check if current user is the owner
        if (user && user.id) {
          const ownerResponse = await RecipeService.isRecipeOwner(id, user.id);
          setIsOwner(ownerResponse.data);
        }
      } catch (err) {
        console.error('Error fetching recipe details:', err);
        setError('Failed to load recipe details. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchRecipeAndComments();
  }, [id, user]);

  const handleSubmitComment = async (e) => {
    e.preventDefault();
    
    if (!user) {
      alert('You must be logged in to post comments.');
      return;
    }
    
    if (!newComment.trim()) {
      return;
    }
    
    try {
      const response = await RecipeService.addComment(id, newComment, user.id);
      setComments([response.data, ...comments]);
      setNewComment('');
    } catch (err) {
      console.error('Error posting comment:', err);
      alert('Failed to post comment. Please try again.');
    }
  };

  const handleDeleteComment = async (commentId) => {
    if (window.confirm('Are you sure you want to delete this comment?')) {
      try {
        await RecipeService.deleteComment(id, commentId, user.id);
        setComments(comments.filter(comment => comment.id !== commentId));
      } catch (err) {
        console.error('Error deleting comment:', err);
        alert('Failed to delete comment. Please try again.');
      }
    }
  };

  const handleDeleteRecipe = async () => {
    if (window.confirm('Are you sure you want to delete this recipe? This action cannot be undone.')) {
      try {
        await RecipeService.deleteRecipe(id, user.id);
        navigate('/recipes');
      } catch (err) {
        console.error('Error deleting recipe:', err);
        alert('Failed to delete recipe. Please try again.');
      }
    }
  };

  if (loading) {
    return <div className="loading">Loading recipe details...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!recipe) {
    return <div className="error">Recipe not found</div>;
  }

  return (
    <div className="recipe-detail-container">
      <div className="recipe-header">
        <div className="back-button">
          <button onClick={() => navigate('/recipes')} className="btn btn-back">
            ← Back to Recipes
          </button>
        </div>
        <h1 className="recipe-title">{recipe.title}</h1>
        <div className="recipe-meta">
          <span className="recipe-author">By: {recipe.userName}</span>
          {recipe.cuisineType && <span className="recipe-cuisine">{recipe.cuisineType}</span>}
          {recipe.prepTime && <span className="prep-time">Prep: {recipe.prepTime} min</span>}
          {recipe.cookTime && <span className="cook-time">Cook: {recipe.cookTime} min</span>}
          {recipe.servingSize && <span className="serving-size">Serves: {recipe.servingSize}</span>}
        </div>
        
        {recipe.dietaryPreferences && (
          <div className="dietary-tags">
            {recipe.dietaryPreferences.split(',').map((pref, idx) => (
              <span key={idx} className="dietary-tag">{pref.trim()}</span>
            ))}
          </div>
        )}
        
        {isOwner && (
          <div className="owner-controls">
            <Link to={`/recipes/edit/${recipe.id}`} className="btn btn-edit">
              Edit Recipe
            </Link>
            <button onClick={handleDeleteRecipe} className="btn btn-delete">
              Delete Recipe
            </button>
          </div>
        )}
      </div>
      
      <div className="recipe-content">
        <div className="recipe-main">
          {recipe.imageUrl && (
            <div className="recipe-image">
              <img src={recipe.imageUrl} alt={recipe.title} />
            </div>
          )}
          
          <div className="recipe-ingredients">
            <h2>Ingredients</h2>
            <ul className="ingredients-list">
              {recipe.ingredients.map((ingredient, idx) => (
                <li key={idx} className="ingredient-item">
                  <span className="ingredient-quantity">{ingredient.quantity}</span>
                  <span className="ingredient-unit">{ingredient.measurementUnit}</span>
                  <span className="ingredient-name">{ingredient.name}</span>
                </li>
              ))}
            </ul>
          </div>
          
          <div className="recipe-instructions">
            <h2>Instructions</h2>
            <div className="instructions-text">
              {recipe.instructions.split('\n').map((paragraph, idx) => (
                <p key={idx}>{paragraph}</p>
              ))}
            </div>
          </div>
        </div>
        
        <div className="recipe-comments">
          <h2>Comments</h2>
          
          {user ? (
            <form onSubmit={handleSubmitComment} className="comment-form">
              <textarea
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder="Add your comment..."
                required
              ></textarea>
              <button type="submit" className="btn btn-submit">Post Comment</button>
            </form>
          ) : (
            <p className="login-prompt">Please <Link to="/">log in</Link> to add comments.</p>
          )}
          
          <div className="comments-list">
            {comments.length === 0 ? (
              <p className="no-comments">No comments yet. Be the first to comment!</p>
            ) : (
              comments.map(comment => (
                <div key={comment.id} className="comment">
                  <div className="comment-header">
                    <span className="comment-author">{comment.userName}</span>
                    <span className="comment-date">
                      {new Date(comment.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                  <p className="comment-content">{comment.content}</p>
                  
                  {user && (user.id === comment.userId || isOwner) && (
                    <button 
                      onClick={() => handleDeleteComment(comment.id)}
                      className="btn-delete-comment"
                    >
                      Delete
                    </button>
                  )}
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RecipeDetailPage;
