import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css'; // Make sure your CSS matches the layout
import RecipeService from '../services/RecipeService';

function Home() {
  const [posts, setPosts] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch posts
    axios.get('/api/posts/')
      .then(response => {
        setPosts(response.data);
      })
      .catch(error => {
        console.error('Error fetching posts:', error);
      });
      
    // Fetch recipes
    RecipeService.getAllRecipes()
      .then(response => {
        setRecipes(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching recipes:', error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="home-container">
      <div className="page-header"></div>

      <div className="action-buttons">
        <button onClick={() => navigate('/create')} className="btn create-post-btn">
          Create New Post
        </button>
        <button onClick={() => navigate('/profile')} className="btn profile-btn">
          Your Profile
        </button>
        <button onClick={() => navigate('/groups')} className="btn groups-btn">
          Community Groups
        </button>
        <button onClick={() => navigate('/recipes')} className="btn recipes-btn">
          Browse Recipes
        </button>
        <button onClick={() => navigate('/recipes/create')} className="btn create-recipe-btn">
          Create Recipe
        </button>
        <button onClick={() => navigate('/recipes/my-recipes')} className="btn my-recipes-btn">
          My Recipes
        </button>
      </div>

      {/* Featured Recipes Section */}
      <div className="featured-section">
        <h2>Featured Recipes</h2>
        <div className="recipes-carousel">
          {loading ? (
            <p>Loading recipes...</p>
          ) : recipes.length > 0 ? (
            recipes.slice(0, 3).map(recipe => (
              <div key={recipe.id} className="recipe-card" onClick={() => navigate(`/recipes/${recipe.id}`)}>
                <div className="recipe-image-container">
                  <img 
                    src={recipe.imageUrl || 'https://via.placeholder.com/300x200?text=No+Image'} 
                    alt={recipe.title} 
                    className="recipe-image" 
                  />
                </div>
                <div className="recipe-info">
                  <h3>{recipe.title}</h3>
                  <p className="cuisine-type">Cuisine: {recipe.cuisineType}</p>
                  <p className="recipe-creator">By: {recipe.userName}</p>
                </div>
              </div>
            ))
          ) : (
            <p>No recipes available. <a href="/recipes/create">Create one?</a></p>
          )}
        </div>
        <button onClick={() => navigate('/recipes')} className="view-all-btn">
          View All Recipes
        </button>
      </div>

      <h2>Recent Posts</h2>
      <div className="posts-container">
        {posts.map(post => {
          const avatarUrl = `https://api.dicebear.com/7.x/thumbs/svg?seed=${encodeURIComponent(post.userName || 'user')}`;
          
          return (
            <div key={post.id} className="post-card">
              <div className="post-header">
                <img src={avatarUrl} alt="avatar" className="avatar" />
                <p className="user-name">{post.userName}</p>
              </div>

              <h3 className="post-title">{post.title}</h3>

              {post.imagePath && (
                <img
                  src={`/uploads/${post.imagePath}`}
                  alt={post.title}
                  className="post-image"
                />
              )}

              <p className="post-description">{post.description}</p>

              <div className="post-actions">
                <button>❤️ Like</button>
                <button>💬 Comment</button>
                <button>🔗 Share</button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Home;
