import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import RecipeService from '../services/RecipeService';
import '../styles/Home.css'; // Make sure your CSS matches the layout
import Navbar from '../components/Navbar';

function Home() {
  const [posts, setPosts] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [loadingRecipes, setLoadingRecipes] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch posts
    axios.get('/api/posts/')
      .then(response => {
        setPosts(response.data);
      })
      .catch(error => {
        console.error('Error fetching posts:', error);
      });

    // Fetch latest recipes
    fetchLatestRecipes();
  }, []);

  const fetchLatestRecipes = () => {
    setLoadingRecipes(true);
    RecipeService.getLatestRecipes()
      .then(response => {
        setRecipes(response.data);
        setLoadingRecipes(false);
      })
      .catch(error => {
        console.error('Error fetching recipes:', error);
        setLoadingRecipes(false);
      });
  };

  const formatDate = (timestamp) => {
    return new Date(timestamp).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <>
      <Navbar />
      <div className="home-container">
        <div className="page-header"></div>

        <div className="action-buttons">
          <button onClick={() => navigate('/create')} className="btn create-post-btn">
            Create New Post
          </button>
          <button onClick={() => navigate('/recipes/create')} className="btn create-recipe-btn">
            Create New Recipe
          </button>
          <button onClick={() => navigate('/profile')} className="btn profile-btn">
            Your Profile
          </button>
          <button onClick={() => navigate('/groups')} className="btn groups-btn">
            Community Groups
          </button>
        </div>

        {/* Latest Recipes Section */}
        <div className="latest-recipes-section">
          <div className="section-header">
            <h2>Latest Recipes</h2>
            <button onClick={() => navigate('/recipes')} className="view-all-btn">View All</button>
          </div>

          {loadingRecipes ? (
            <p>Loading recipes...</p>
          ) : (
            <div className="recipes-slider">
              {recipes.length === 0 ? (
                <p>No recipes found.</p>
              ) : (
                recipes.slice(0, 3).map(recipe => (
                  <div key={recipe.id} className="recipe-card-home" onClick={() => navigate(`/recipes/${recipe.id}`)}>
                    <img
                      src={recipe.imageUrl || 'https://via.placeholder.com/300x200?text=No+Image'}
                      alt={recipe.title}
                      className="recipe-card-image"
                    />
                    <div className="recipe-card-content">
                      <h3 className="recipe-card-title">{recipe.title}</h3>
                      {recipe.cuisine && (
                        <span className="recipe-card-cuisine">{recipe.cuisine}</span>
                      )}
                      <div className="recipe-card-info">
                        <span>By {recipe.userName}</span>
                        <span>{formatDate(recipe.createdAt)}</span>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          )}
        </div>

        <h2>Latest Posts</h2>
        <div className="posts-container">
          {posts.map(post => {
            const avatarUrl = `https://api.dicebear.com/7.x/thumbs/svg?seed=${encodeURIComponent(post.userName || 'user')}`;

            return (
              <div key={post.id} className="post-card">
                <div className="post-header">
                  <img src={avatarUrl} alt="avatar" className="avatar" />
                  <p className="user-name">{post.userName}</p>
                </div>

                <h3 className="post-title">{post.title}</h3>

                {post.imagePath && (
                  <img
                    src={`/uploads/${post.imagePath}`}
                    alt={post.title}
                    className="post-image"
                  />
                )}

                <p className="post-description">{post.description}</p>

                <div className="post-actions">
                  <button>‚ù§Ô∏è Like</button>
                  <button>üí¨ Comment</button>
                  <button>üîó Share</button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}

export default Home;
