import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import GroupService from '../services/GroupService';
import GroupNav from './GroupNav';
import { useUser } from '../pages/UserContext';
import '../styles/GroupList.css';

const AllGroups = () => {
  const [groups, setGroups] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const { user } = useUser();
  
  // Get current user ID from context
  const currentUserId = user ? user.id : null;

  useEffect(() => {
    loadGroups();
  }, []);

  const loadGroups = async () => {
    setLoading(true);
    try {
      const response = await GroupService.getAllGroups();
      setGroups(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to load groups. Please try again later.');
      console.error('Error loading groups:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) {
      loadGroups();
      return;
    }
    
    setLoading(true);
    try {
      const response = await GroupService.searchGroups(searchQuery);
      setGroups(response.data);
      setError(null);
    } catch (err) {
      setError('Search failed. Please try again.');
      console.error('Error searching groups:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleJoinGroup = async (groupId) => {
    try {
      await GroupService.joinGroup(groupId, currentUserId);
      // Refresh the current group's data
      const updatedGroups = groups.map(group => {
        if (group.id === groupId) {
          return { ...group, memberCount: group.memberCount + 1 };
        }
        return group;
      });
      setGroups(updatedGroups);
    } catch (err) {
      console.error('Error joining group:', err);
      alert('Failed to join group. Please try again.');
    }
  };

  return (
    <div className="group-list-container">
      <GroupNav currentPage="all" />
      
      <h2 className="page-title">Discover Groups</h2>
      <p className="page-description">
        Find and join groups based on your culinary interests and connect with other food enthusiasts.
      </p>
      
      <div className="group-search">
        <form onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Search by name or cuisine type..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {loading ? (
        <div className="loading">Loading groups...</div>
      ) : groups.length === 0 ? (
        <div className="no-groups">
          <p>No groups found. Be the first to create one!</p>
          <button 
            className="primary-btn"
            onClick={() => navigate('/groups/create')}
          >
            Create a Group
          </button>
        </div>
      ) : (
        <div className="group-cards">
          {groups.map(group => (
            <div key={group.id} className="group-card">
              <div className="group-card-image">
                {group.imageUrl ? (
                  <img src={group.imageUrl} alt={group.name} />
                ) : (
                  <div className="group-card-image-placeholder">
                    <i className="fa fa-cutlery"></i>
                  </div>
                )}
              </div>
              <div className="group-card-content">
                <h3>{group.name}</h3>
                <p className="cuisine-type">Cuisine: {group.cuisineType}</p>
                <p className="description">{group.description}</p>
                <div className="group-meta">
                  <span><i className="fa fa-users"></i> {group.memberCount} members</span>
                  <span><i className="fa fa-user"></i> {group.adminName}</span>
                </div>
                <div className="group-card-actions">
                  <button 
                    className="view-btn"
                    onClick={() => navigate(`/groups/${group.id}`)}
                  >
                    View Details
                  </button>
                  
                  {group.adminId !== currentUserId && (
                    <button 
                      className="join-btn"
                      onClick={() => handleJoinGroup(group.id)}
                    >
                      Join Group
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default AllGroups;
