import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import RecipeService from '../services/RecipeService';
import { UserContext } from './UserContext';

const EditRecipe = () => {
  const { recipeId } = useParams();
  const navigate = useNavigate();
  const { user } = useContext(UserContext);
  
  const [recipe, setRecipe] = useState({
    title: '',
    description: '',
    instructions: '',
    imageUrl: '',
    cuisineType: '',
    dietaryPreferences: [],
    ingredients: [{ name: '', quantity: '' }]
  });
  
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);
  
  const cuisineTypes = [
    'American', 'Italian', 'Mexican', 'Chinese', 'Indian', 
    'Japanese', 'Thai', 'Mediterranean', 'French', 'Greek'
  ];
  
  const dietaryOptions = [
    'Vegetarian', 'Vegan', 'Gluten-Free', 'Dairy-Free', 
    'Nut-Free', 'Low-Carb', 'Keto', 'Paleo'
  ];
  
  useEffect(() => {
    if (!user) {
      navigate('/');
      return;
    }
    
    fetchRecipeData();
  }, [recipeId, user, navigate]);
  
  const fetchRecipeData = () => {
    setLoading(true);
    RecipeService.getRecipeById(recipeId)
      .then(response => {
        const fetchedRecipe = response.data;
        
        // Check if the user is the owner
        if (user.id !== fetchedRecipe.userId) {
          setError('You do not have permission to edit this recipe');
          setLoading(false);
          return;
        }
        
        setRecipe(fetchedRecipe);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching recipe:', error);
        setError('Recipe not found or could not be loaded');
        setLoading(false);
      });
  };
  
  // Handle text input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setRecipe({
      ...recipe,
      [name]: value
    });
  };
  
  // Handle dietary preferences changes
  const handleDietaryChange = (preference) => {
    const updatedPreferences = [...recipe.dietaryPreferences];
    
    if (updatedPreferences.includes(preference)) {
      // Remove if already selected
      const index = updatedPreferences.indexOf(preference);
      updatedPreferences.splice(index, 1);
    } else {
      // Add if not selected
      updatedPreferences.push(preference);
    }
    
    setRecipe({
      ...recipe,
      dietaryPreferences: updatedPreferences
    });
  };
  
  // Handle ingredient changes
  const handleIngredientChange = (index, field, value) => {
    const updatedIngredients = [...recipe.ingredients];
    updatedIngredients[index] = {
      ...updatedIngredients[index],
      [field]: value
    };
    
    setRecipe({
      ...recipe,
      ingredients: updatedIngredients
    });
  };
  
  // Add new ingredient field
  const addIngredient = () => {
    setRecipe({
      ...recipe,
      ingredients: [...recipe.ingredients, { name: '', quantity: '' }]
    });
  };
  
  // Remove ingredient field
  const removeIngredient = (index) => {
    if (recipe.ingredients.length === 1) {
      return; // Keep at least one ingredient
    }
    
    const updatedIngredients = [...recipe.ingredients];
    updatedIngredients.splice(index, 1);
    
    setRecipe({
      ...recipe,
      ingredients: updatedIngredients
    });
  };
  
  // Validate form
  const validateForm = () => {
    const newErrors = {};
    
    if (!recipe.title.trim()) newErrors.title = 'Title is required';
    if (!recipe.description.trim()) newErrors.description = 'Description is required';
    if (!recipe.instructions.trim()) newErrors.instructions = 'Instructions are required';
    if (!recipe.cuisineType) newErrors.cuisineType = 'Cuisine type is required';
    
    // Check if all ingredients have both name and quantity
    recipe.ingredients.forEach((ingredient, index) => {
      if (!ingredient.name.trim() || !ingredient.quantity.trim()) {
        newErrors.ingredients = 'All ingredients must have both name and quantity';
      }
    });
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  
  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setIsSubmitting(true);
    
    RecipeService.updateRecipe(recipeId, recipe, user.id)
      .then(response => {
        setIsSubmitting(false);
        navigate(`/recipes/${recipeId}`);
      })
      .catch(error => {
        setIsSubmitting(false);
        console.error('Error updating recipe:', error);
        alert('Failed to update recipe. Please try again.');
      });
  };
  
  if (loading) {
    return <div className="loading">Loading recipe...</div>;
  }
  
  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => navigate(`/recipes/${recipeId}`)}>
          Back to Recipe
        </button>
      </div>
    );
  }
  
  return (
    <div className="edit-recipe-container">
      <div className="edit-recipe-header">
        <h1>Edit Recipe</h1>
        <button 
          onClick={() => navigate(`/recipes/${recipeId}`)} 
          className="back-btn"
        >
          Cancel
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="recipe-form">
        <div className="form-group">
          <label htmlFor="title">Recipe Title*</label>
          <input
            type="text"
            id="title"
            name="title"
            value={recipe.title}
            onChange={handleChange}
            placeholder="Enter recipe title..."
            className={errors.title ? 'error' : ''}
          />
          {errors.title && <span className="error-message">{errors.title}</span>}
        </div>
        
        <div className="form-group">
          <label htmlFor="description">Description*</label>
          <textarea
            id="description"
            name="description"
            value={recipe.description}
            onChange={handleChange}
            placeholder="Briefly describe your recipe..."
            rows="3"
            className={errors.description ? 'error' : ''}
          />
          {errors.description && <span className="error-message">{errors.description}</span>}
        </div>
        
        <div className="form-group">
          <label htmlFor="instructions">Instructions*</label>
          <textarea
            id="instructions"
            name="instructions"
            value={recipe.instructions}
            onChange={handleChange}
            placeholder="Detailed step-by-step instructions..."
            rows="6"
            className={errors.instructions ? 'error' : ''}
          />
          {errors.instructions && <span className="error-message">{errors.instructions}</span>}
        </div>
        
        <div className="form-group">
          <label htmlFor="imageUrl">Image URL</label>
          <input
            type="text"
            id="imageUrl"
            name="imageUrl"
            value={recipe.imageUrl}
            onChange={handleChange}
            placeholder="Enter image URL..."
          />
          {recipe.imageUrl && (
            <div className="image-preview">
              <img src={recipe.imageUrl} alt="Recipe preview" />
            </div>
          )}
        </div>
        
        <div className="form-group">
          <label htmlFor="cuisineType">Cuisine Type*</label>
          <select
            id="cuisineType"
            name="cuisineType"
            value={recipe.cuisineType}
            onChange={handleChange}
            className={errors.cuisineType ? 'error' : ''}
          >
            <option value="">Select Cuisine Type</option>
            {cuisineTypes.map(cuisine => (
              <option key={cuisine} value={cuisine}>{cuisine}</option>
            ))}
          </select>
          {errors.cuisineType && <span className="error-message">{errors.cuisineType}</span>}
        </div>
        
        <div className="form-group dietary-options">
          <label>Dietary Preferences</label>
          <div className="checkbox-group">
            {dietaryOptions.map(option => (
              <label key={option} className="checkbox-label">
                <input
                  type="checkbox"
                  checked={recipe.dietaryPreferences.includes(option)}
                  onChange={() => handleDietaryChange(option)}
                />
                {option}
              </label>
            ))}
          </div>
        </div>
        
        <div className="form-group ingredients-section">
          <label>Ingredients*</label>
          {recipe.ingredients.map((ingredient, index) => (
            <div key={index} className="ingredient-row">
              <input
                type="text"
                placeholder="Ingredient name"
                value={ingredient.name}
                onChange={(e) => handleIngredientChange(index, 'name', e.target.value)}
                className={errors.ingredients ? 'error' : ''}
              />
              <input
                type="text"
                placeholder="Quantity (e.g., 2 cups)"
                value={ingredient.quantity}
                onChange={(e) => handleIngredientChange(index, 'quantity', e.target.value)}
                className={errors.ingredients ? 'error' : ''}
              />
              <button 
                type="button" 
                onClick={() => removeIngredient(index)}
                className="remove-btn"
              >
                Remove
              </button>
            </div>
          ))}
          {errors.ingredients && <span className="error-message">{errors.ingredients}</span>}
          <button 
            type="button" 
            onClick={addIngredient}
            className="add-btn"
          >
            Add Ingredient
          </button>
        </div>
        
        <div className="form-actions">
          <button type="submit" className="submit-btn" disabled={isSubmitting}>
            {isSubmitting ? 'Saving Changes...' : 'Save Changes'}
          </button>
          <button 
            type="button" 
            onClick={() => navigate(`/recipes/${recipeId}`)}
            className="cancel-btn"
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditRecipe;
