import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useUser } from './UserContext';
import RecipeService from '../services/RecipeService';
import '../styles/RecipeForm.css';

const EditRecipePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useUser();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [recipe, setRecipe] = useState({
    title: '',
    instructions: '',
    imageUrl: '',
    cuisineType: '',
    dietaryPreferences: '',
    prepTime: '',
    cookTime: '',
    servingSize: '',
    ingredients: []
  });

  useEffect(() => {
    if (!user) {
      navigate('/');
      return;
    }

    const fetchRecipe = async () => {
      try {
        const response = await RecipeService.getRecipeById(id);
        const recipeData = response.data;
        
        // Check if user is the owner
        const ownerCheck = await RecipeService.isRecipeOwner(id, user.id);
        if (!ownerCheck.data) {
          setError('You do not have permission to edit this recipe');
          return;
        }
        
        // Set recipe data
        setRecipe({
          ...recipeData,
          prepTime: recipeData.prepTime || '',
          cookTime: recipeData.cookTime || '',
          servingSize: recipeData.servingSize || '',
          ingredients: recipeData.ingredients.length > 0 
            ? recipeData.ingredients 
            : [{ name: '', quantity: '', measurementUnit: '' }]
        });
      } catch (err) {
        console.error('Error fetching recipe:', err);
        setError('Failed to load recipe. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [id, user, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Validate required fields
      if (!recipe.title || !recipe.instructions) {
        setError('Title and instructions are required.');
        setLoading(false);
        return;
      }

      // Validate ingredients
      const validIngredients = recipe.ingredients.filter(
        ing => ing.name && ing.quantity && ing.measurementUnit
      );
      
      if (validIngredients.length === 0) {
        setError('At least one complete ingredient is required.');
        setLoading(false);
        return;
      }

      // Prepare data for submission
      const recipeData = {
        ...recipe,
        prepTime: parseInt(recipe.prepTime) || 0,
        cookTime: parseInt(recipe.cookTime) || 0,
        servingSize: parseInt(recipe.servingSize) || 0,
        ingredients: validIngredients
      };

      // Submit the updated recipe
      await RecipeService.updateRecipe(id, recipeData, user.id);
      navigate(`/recipes/${id}`);
    } catch (err) {
      console.error('Error updating recipe:', err);
      setError('Failed to update recipe. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRecipe({ ...recipe, [name]: value });
  };

  const handleIngredientChange = (index, e) => {
    const { name, value } = e.target;
    const updatedIngredients = [...recipe.ingredients];
    updatedIngredients[index] = { ...updatedIngredients[index], [name]: value };
    setRecipe({ ...recipe, ingredients: updatedIngredients });
  };

  const addIngredient = () => {
    setRecipe({
      ...recipe,
      ingredients: [...recipe.ingredients, { name: '', quantity: '', measurementUnit: '' }]
    });
  };

  const removeIngredient = (index) => {
    if (recipe.ingredients.length > 1) {
      const updatedIngredients = recipe.ingredients.filter((_, i) => i !== index);
      setRecipe({ ...recipe, ingredients: updatedIngredients });
    }
  };

  if (loading) {
    return <div className="loading">Loading recipe...</div>;
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error-message">{error}</div>
        <button onClick={() => navigate('/recipes')} className="btn btn-back">
          Back to Recipes
        </button>
      </div>
    );
  }

  return (
    <div className="recipe-form-container">
      <div className="form-header">
        <h1>Edit Recipe</h1>
        <button onClick={() => navigate(`/recipes/${id}`)} className="btn btn-back">
          Cancel
        </button>
      </div>
      
      <form onSubmit={handleSubmit} className="recipe-form">
        <div className="form-group">
          <label htmlFor="title">Recipe Title*</label>
          <input
            id="title"
            name="title"
            type="text"
            value={recipe.title}
            onChange={handleChange}
            required
            placeholder="Enter recipe title"
          />
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="cuisineType">Cuisine Type</label>
            <input
              id="cuisineType"
              name="cuisineType"
              type="text"
              value={recipe.cuisineType || ''}
              onChange={handleChange}
              placeholder="E.g. Italian, Mexican, Thai"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="dietaryPreferences">Dietary Preferences</label>
            <input
              id="dietaryPreferences"
              name="dietaryPreferences"
              type="text"
              value={recipe.dietaryPreferences || ''}
              onChange={handleChange}
              placeholder="E.g. Vegetarian, Vegan, Gluten-Free (comma separated)"
            />
          </div>
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="prepTime">Prep Time (minutes)</label>
            <input
              id="prepTime"
              name="prepTime"
              type="number"
              min="0"
              value={recipe.prepTime}
              onChange={handleChange}
              placeholder="Prep time in minutes"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="cookTime">Cook Time (minutes)</label>
            <input
              id="cookTime"
              name="cookTime"
              type="number"
              min="0"
              value={recipe.cookTime}
              onChange={handleChange}
              placeholder="Cook time in minutes"
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="servingSize">Serving Size</label>
            <input
              id="servingSize"
              name="servingSize"
              type="number"
              min="1"
              value={recipe.servingSize}
              onChange={handleChange}
              placeholder="Number of servings"
            />
          </div>
        </div>
        
        <div className="form-group">
          <label htmlFor="imageUrl">Image URL</label>
          <input
            id="imageUrl"
            name="imageUrl"
            type="url"
            value={recipe.imageUrl || ''}
            onChange={handleChange}
            placeholder="Enter URL for recipe image"
          />
        </div>
        
        <div className="form-group">
          <label>Ingredients*</label>
          <div className="ingredients-list">
            {recipe.ingredients.map((ingredient, index) => (
              <div key={index} className="ingredient-row">
                <div className="ingredient-name">
                  <input
                    name="name"
                    type="text"
                    value={ingredient.name || ''}
                    onChange={(e) => handleIngredientChange(index, e)}
                    placeholder="Ingredient name"
                    required={index === 0}
                  />
                </div>
                <div className="ingredient-quantity">
                  <input
                    name="quantity"
                    type="text"
                    value={ingredient.quantity || ''}
                    onChange={(e) => handleIngredientChange(index, e)}
                    placeholder="Amount"
                    required={index === 0}
                  />
                </div>
                <div className="ingredient-unit">
                  <input
                    name="measurementUnit"
                    type="text"
                    value={ingredient.measurementUnit || ''}
                    onChange={(e) => handleIngredientChange(index, e)}
                    placeholder="Unit (g, ml, tbsp)"
                    required={index === 0}
                  />
                </div>
                <button 
                  type="button" 
                  onClick={() => removeIngredient(index)}
                  className="btn-remove-ingredient"
                  disabled={recipe.ingredients.length <= 1 && index === 0}
                >
                  âœ•
                </button>
              </div>
            ))}
            <button type="button" onClick={addIngredient} className="btn btn-add-ingredient">
              + Add Ingredient
            </button>
          </div>
        </div>
        
        <div className="form-group">
          <label htmlFor="instructions">Instructions*</label>
          <textarea
            id="instructions"
            name="instructions"
            value={recipe.instructions}
            onChange={handleChange}
            rows="10"
            required
            placeholder="Enter step-by-step instructions"
          ></textarea>
        </div>
        
        <div className="form-actions">
          <button 
            type="button" 
            onClick={() => navigate(`/recipes/${id}`)} 
            className="btn btn-cancel"
          >
            Cancel
          </button>
          <button 
            type="submit" 
            className="btn btn-submit" 
            disabled={loading}
          >
            {loading ? 'Saving...' : 'Save Changes'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditRecipePage;
